css:
@-moz-document domain("7cups.com") {
/*-----------hue value=257-----------*/

body {
    background-color: #4cfa6c !important;
}
.navbar {
    background: #FA33E5 !important;
    background-image: none;
  }
#bottomStatus,
.bottomStatus {
    color: #fff !important;
    background-color: #FA33E5 !important;
}
#bottomStatus.heavyLoad {
    color: #FA33E5 !important;
    background-color: #FA33E5 !important;
    background: #FA33E5 !important;
}
.btn-default,
.btn-primary {
    color: #000000 !important;
    background-color: #FCFE50 !important;
}
.card .nav-link,
.active {
    color: #FA33E5 !important;
    background-color: #fefefe !important;
}
.card {
    background-color: #fefefe !important;
}
.convHolder,
#actionButtons,
#actionButtons img {
    background-color: #fefefe !important;
}
.convHolder .note {
    color: #FA33E5 !important;
}
.card-title {
    color: #FA33E5 !important;
}
textarea {
    background-color: #fefefe !important;
    color: #FCFE50 !important;
}
.chatBox .youWrap {
    background-color: #d6fee2;
}
.chatBox .meWrap {
    color: #000000 !important;
    background-color: #affe99;
}
.text-center,
.details {
    color: #FCFE50 !important;
}
.userImage { border-radius: 10%; 
}
