css:
@-moz-document url-prefix("https://soundcloud"), url-prefix("http://soundcloud"), domain("soundcloud.com") {
.playControls__inner
{ color: orange!important; background: #000!important; border-top: 1px orange solid!important; }

.l-container #content
{ color: orange!important; background: #000!important; border-left: 1px #634003 solid!important; border-right: 1px #634003 solid!important; }

header
{ color: orange!important; background: #000!important; border-bottom: 2px #634003 solid!important; }

[aria-role="img"]
{ border-radius: 10px; }

.sc-artwork.sc-artwork-placeholder-0, .sc-artwork.sc-artwork-placeholder-1, .sc-artwork.sc-artwork-placeholder-2, .sc-artwork.sc-artwork-placeholder-3, .sc-artwork.sc-artwork-placeholder-4, .sc-artwork.sc-artwork-placeholder-5, .sc-artwork.sc-artwork-placeholder-6, .sc-artwork.sc-artwork-placeholder-7, .sc-artwork.sc-artwork-placeholder-8, .sc-artwork.sc-artwork-placeholder-9, .sc-artwork.sc-artwork-placeholder-10, .sc-artwork.sc-artwork-placeholder-11
{ background: transparent; }

.conversation__actions, .listenEngagement, .conversation__form
{ box-shadow: none !important; }

.sc-button-small.sc-button-like:before, .sc-button-small.sc-button-share:before, .sc-button-small.sc-button-repost:before, .sc-button-small.sc-button-addtoset:before, .sc-button-medium.sc-button-message:before, .sc-ministats-large.sc-ministats-followers:before, .sc-ministats-large.sc-ministats-sounds:before, .sc-button-medium.sc-button-addtogroup:before, .sc-button-medium.sc-button-addtoset:before, .sc-button-medium.sc-button-delete:before, .sc-button-medium.sc-button-download:before, .sc-button-medium.sc-button-edit:before, .sc-button-medium.sc-button-instagram:before, .sc-button-medium.sc-button-like:before, .sc-button-medium.sc-button-message:before, .sc-button-medium.sc-button-moderate:before, .sc-button-medium.sc-button-pc:before, .sc-button-medium.sc-button-reply:before, .sc-button-medium.sc-button-repost:before, .sc-button-medium.sc-button-share:before, .sc-button-small.sc-button-delete:before, .sc-button-medium.sc-button-icon:before
{ -webkit-filter: brightness(5); }

.playControls__playPauseSkip, .playControls__repeat, .volume__iconWrapper, .playbackTimeline__duration
{ -webkit-filter: brightness(5) !important; }

.sc-background-light, .sc-border-light-right, .sc-border-light-bottom, .sc-button-toolbar > .sc-button-group, .sc-button, .sc-border-light-top, .sc-border-light, .sc-tag, .sc-tag:visited, .sc-ministats-group .sc-ministats, .ownActivity.large .ownActivity__user, .g-tabs, .collection.m-overview .collection__section, .genericBadgePlaceholder:before
{ border-color: #323232; }

textarea, select, input, textarea:focus, select:focus, input[type="text"]:focus, input[type="email"]:focus, input[type="tel"]:focus, input[type="url"]:focus, input[type="date"]:focus, input[type="password"]:focus, input[type="search"]:focus, .dashbox__box, .commentForm.small, .commentForm__input, .commentForm__input:focus, .commentForm.large .commentForm__inputWrapper, .focused .commentForm.small, .commentForm, .listenEngagement, .listenInfoModal__heading, .sc-toggle, .g-form-section-head, .sidebarModule.collapsed .sidebarHeader, .gritter-item-wrapper, .sc-border-light-left, .sc-border-light-bottom, .sc-border-light-top, .archive-title, .page-title, .widget-title, .entry-content th, .comment-content th, .l-listen-wrapper .l-about-rows, .widget-area .widget, .searchTitle__content, .searchTitle, .moreActions, .moreActions__button:not(:last-child), .moreActions__link
{ border-color: #323232 !important; }

.productsMiniTable__cell:nth-child(even), .productsMiniTable__cell:nth-child(odd)
{ border-color: #202020 !important; }

#header, body #main-wrapper, body #main-content-inner, body .one-col #main-wrapper-inner, .g-dark textarea, .g-dark select
{ background: #000 !important; }

.g-dark input
{ border: 1px #634003 solid!important; background: #000 !important; }

.sound__soundActions, .userNetworkTop__inner, .modal__modal, .modal__content .page, .page, .additional-footer, .site, .listenNetworkTop__inner, .searchTitle__content, .searchTitle, .listenContent__inner
{ background: #1a1a1a !important; }

.sc-background-light, .sc-tag, .sc-tag:visited, .activeUpload__form, .tagInput__wrapper, .editAccessTab__quietTeaser, .exploreCategoryList__list, .categoriesButton, .exploreCategoryList, .l-fixed-top-one-column>.l-top, .moreActions__button, .moreActions__link
{ background: #202020; }

#side-content .settings-sidebar ul.tabs li a .sub, #side-content .settings-sidebar ul.tabs li a span.title, #side-content .settings-sidebar ul.tabs li.current a span, #side-content .settings-sidebar ul.tabs li:hover a span, body#tokens .listing tr.even, body#client_applications .listing tr.even, .commentForm.small, .commentForm.large .commentForm__inputWrapper, .commentForm, .focused .commentForm.small, .compactTrackListItem.clickToPlay.active, .compactTrackListItem.clickToPlay:hover, .compactTrackListItem.clickToPlay:focus, .compactTrackList__moreLink:hover, .compactTrackList__moreLink:focus, .trackItem.hover, .trackItem.active, .trackItem.hover .trackItem__additional, .trackItem.active .trackItem__additional, .g-modal-section, .uploadMain__foot, .uploadMain__chooserContainer, .conversation__form, .inboxItem:hover, .gritter-item-wrapper, .productsMiniTable__planPreviewPrizing, .sidebarInfoBox__body, .addToPlaylistTabs .tabs__tabs, .addToPlaylistTabs .tabs__headingContainer, .createPlaylistSuggestion__addContainer, .addToPlaylistItem__actions, .search__headerInner
{ background: #202020 !important; }

textarea, select, input[type="text"], input[type="email"], input[type="tel"], input[type="url"], input[type="date"], input[type="password"], input[type="search"], .commentForm__input, .modal__modal.transparentBackground, .announcement, .readMoreBadge__countWrapper, .moreActions__button:hover, .moreActions__link:hover, .moreActions__button:focus
{ background: #000; }

select, .conversation__actions, .inboxItem.active, .productsMiniTable__planPreviewList, .announcement, .trackItemWithEdit.hover, .trackItemWithEdit.active
{ background: #000 !important; }

.sc-toggle-handle, .volume__sliderWrapper, .dropbar__content
{ background: #000 !important; }

.l-footer, .compactTrackListItem.clickToPlay.active .compactTrackListItem__additional, .compactTrackListItem.clickToPlay:hover .compactTrackListItem__additional, .compactTrackListItem.clickToPlay:focus .compactTrackListItem__additional, .sc-toggle
{ background: transparent !important; }

.compactTrackListItem__additional, .trackItem__additional
{ background: linear-gradient(to right, rgba(20, 20, 20, .1), #202020 17px) !important; }

.trackItemWithEdit__additional
{ background: linear-gradient(to right, rgba(255, 255, 255, .1), #252525 17px) !important; }

.sc-button-small
{ box-shadow: 0 1px 2px rgba(0, 0, 0, .02), inset 0 1px 0 rgba(40, 40, 40, 1), inset -1px 0 0 rgba(40, 40, 40, 1), inset 0 -1px 0 rgba(40, 40, 40, .5), inset 1px 0 0 rgba(40, 40, 40, 1); }

.sc-button
{ border: 1px solid #252525; background-color: #202020; background-image: -webkit-linear-gradient(rgba(26, 26, 26, 0), rgba(26, 26, 26, .3) 60%, rgba(26, 26, 26, .6)); background-image: linear-gradient(rgba(26, 26, 26, 0), rgba(26, 26, 26, .3) 60%, rgba(26, 26, 26, .6)); text-shadow: none; box-shadow: none; }

.sc-button:hover, .sc-button:focus, .sc-button-focus
{ border: 1px solid #282828; background-color: #323232; background-image: -webkit-linear-gradient(rgba(26, 26, 26, 0), rgba(26, 26, 26, .3) 60%, rgba(26, 26, 26, .6)); background-image: linear-gradient(rgba(26, 26, 26, 0), rgba(26, 26, 26, .3) 60%, rgba(26, 26, 26, .6)); text-shadow: none; box-shadow: none; }

.sc-button-play, .sc-button-pause, .sc-button-prev, .sc-button-next, .sc-button.sc-button-play:disabled, .sc-button.sc-button-play.sc-button-disabled:disabled
{ border-top-width: 0; border-color: rgba(255, 102, 0, .75) rgba(255, 51, 0, .25) rgba(255, 17, 0, .25); background-color: #f50; border-radius: 50%; background-image: -webkit-linear-gradient(rgba(255, 153, 0, .6), rgba(255, 102, 0, .6) 40%, rgba(255, 17, 0, .6)); background-image: linear-gradient(rgba(255, 153, 0, .6), rgba(255, 102, 0, .6) 40%, rgba(255, 17, 0, .6)); box-shadow: 0 0 2px rgba(255, 51, 0, .35), inset 0 -1px 3px rgba(255, 51, 0, 0.45), 0 0 3px rgba(0, 0, 0, .15), inset 0 1px 0 rgba(102, 51, 0, .2), inset 0 2px 2px rgba(255, 255, 255, .25), inset 0 -2px 2px rgba(255, 255, 255, .2); text-shadow: none; }

.sc-button-active, .sc-button-active:hover, .sc-button-active:focus, .sc-button-active.sc-button-focus, .sc-button-selected, .sc-button-selected:hover, .sc-button-selected:focus, .sc-button-selected.sc-button-focus
{ border: 1px solid #bd663a; background-color: #bd663a; }

.listenInfoTeaser__descriptionFade
{ background: -webkit-linear-gradient(rgba(40, 40, 40, 0), rgba(40, 40, 40, 1)) !important; background: linear-gradient(rgba(40, 40, 40, 0), rgba(40, 40, 40, 1)) !important; }

.sc-button:disabled, .sc-button:disabled:hover, .sc-button-disabled, .sc-button-disabled:hover, .sc-button-disabled:focus, .sc-button.sc-pending, .sc-button.sc-pending:hover
{ background-color: #323232; background-image: linear-gradient(#404040, #282828, #404040); box-shadow: none; border-color: #323232; color: orange; text-shadow: 0 1px 0 #fff; }

.modal
{ background-color: rgba(40, 40, 40, .9) !important; }

.truncatedAudioInfo.m-overflow.m-collapsed .truncatedAudioInfo__wrapper::after
{ background: linear-gradient(rgba(47, 47, 47, 0), rgba(77, 77, 77, 0.5)90%, #000) !important; }

.soundBadge__additional
{ background: rgba(73, 71, 71) !important; background: linear-gradient(to right, rgba(73, 71, 71, 0.1), #2F2F2F 17px) !important; }

.truncatedUserDescription.m-overflow.m-collapsed .truncatedUserDescription__wrapper::after
{ background: linear-gradient(rgba(255, 255, 255, 0), #282828) !important; }

.trackItemWithEdit.hover .trackItemWithEdit__additional, .trackItemWithEdit.active .trackItemWithEdit__additional
{ background: linear-gradient(to right, rgba(242, 242, 242, .1), #252525 17px) !important; }

html, body, form, fieldset, a.sc-link-dark, a.sc-ministats-large, .g-dark textarea, .g-dark select, .g-dark input[type="text"], .g-dark input[type="password"], .g-dark input[type="search"], .g-nav-item > .g-nav-link, .g-tabs-link, .g-tabs-link:visited, .dashbox__titleLine, .compactTrackListItem__number, .compactTrackListItem__trackTitle, .userInfo.compactActions .userInfo__title, .userInfo__facts .userInfo__title, .sc-ministats-large, .sc-button:hover, .sc-button:focus, .sc-button-focus, .sc-button, .sc-text, .sc-tag, .sc-tag:visited
{ color: orange; }

textarea, select, button, input, label, #side-content .settings-sidebar ul.tabs li a span, .compactTrackListItem__number, .compactTrackListItem__trackTitle, .permalinkTextfield__input:disabled, .composeMessage .textfield__input, .commentPopover.darkText .commentPopover__body, .gritter-item-wrapper, .sc-input, .product-details-list li, .sc-type-h1, .additional-footer .paymentInfo
{ color: orange !important; }

.sc-buylink, .sc-buylink:visited, .staticPage li a:only-child, .staticPage p a:not(.sc-button), .staticPage li a
{ color: #2f8cb7; }

a.sc-link-dark:hover, a.sc-link-light:hover, a.sc-link-medium:hover, .sc-buylink:hover
{ color: #f50; }

.compactTrackListItem.active .compactTrackListItem__trackTitle
{ color: #f50 !important; }

body
{ background: #000; }

.l-fluid-fixed .playlist>.sound__coverArt, .l-collection .playlist>.sound__coverArt
{ background: transparent; }

.sc-border-light-right
{ border-right: 1px solid transparent !important; }

.volume__sliderBackground
{ background-color: #000 !important; }

.volume__sliderWrapper
{ background-color: #111111 !important; }

.volume.expanded .volume__sliderWrapper
{ border-color: #111111 !important; background-color: #111111 !important; }

.volume__sliderWrapper::before, .volume__sliderWrapper::after
{ border: 5px solid #111111 !important; border-color: transparent transparent #111111 #111111 !important; }
}
