css:
@-moz-document domain("thepunkeffect.com") {
/*Login Box Full Width*/

#loginelements li a {
    color: #ffffff;
}

#loginelements li a:hover {
    color: #c2c2c2;
}

#loginelements {
	background-color: #121212;
}

/*Header Stuff*/
#menuwrapper, 
#headermenu {
	background: #080808;
}

header {
	background: #001b1d;
}

#headermenulist li a {
	color: #ffffff;
}

nav ul li:hover {
	background: #181818;
}

nav ul li:hover ul {
	background: #080808;
}

nav li ul li > a {
    color: #ffffff !important;
}

/*General Page Colors*/

body {
	background-color: #101010;
}

#hometop {
	background: #080808;
}

.heroinfoinside {
	background: #1d1d1d;
}

.heroinfo h2 a {
	color: #ffffff;
}

.sectionheading, #respond h3 {
	color: #ffffff;
	background: #1d1d1d;
}

.footeritem:hover h2, .sidebaritem:hover h2 {
    color: #c2c2c2;
    background: #080808;
}

.listitem span {
	color: #ffffff;
}

.listitem, .archiveitem {
	background: #1d1d1d;
}

.listitem:hover, .archiveitem:hover {
	background: #101010;
}

.listitem:hover span {
	color: #c2c2c2;
}

.videomorebox {
	background: #1d1d1d;
}

main.nothome {
	background: #080808;
	color: #ffffff;
}

.archiveitem {
	color: #ffffff;
}

.archiveitem:hover {
	color: #c2c2c2;
}

.archiveitem:hover .archivetitle a {
    color: #c2c2c2 !important;
}

.postcontent {
	color: #ffffff;
	background: #1d1d1d;
}

.postcontent p:first-child {
	color: #ffffff;
}

.postcontent a {
	color: #c1fbff;
}
}
/*[[PodBeanPlayer]]*/

style_setting_options.value:
@-moz-document url-prefix("https://www.podbean.com/media/player/") {
/*PodBean Player*/
body .pd_player_wrapper {
	border-color: #1d1d1d;
    background: #1d1d1d !important;
}
.pd_player_wrapper .media_title a:link, .pd_player_wrapper .media_title a:visited, .pd_player_wrapper .social_panel .inner .btns .title_panel, .pd_player_wrapper .social_panel .inner .btns a.copy {
    color: #ffffff;
}
.pd_player_wrapper .channel_name a,
.pd_player_wrapper .channel_name {
    color: rgba(255, 255, 255, 0.75);
}
.jp-interface .jp-time-holder {
    color: #ffffff;
}
a:link, a:visited {
    color: #fff;
}
.pd_player_wrapper .logo img {
    -webkit-filter: brightness(0%) invert(100%);
}
.pd_player_wrapper .media_title {
	color: #ffffff;
}
}
