css:
@-moz-document url-prefix("http://www.jeuxvideo.com"), url-prefix("https://www.jeuxvideo.com"), domain("jeuxvideo.com") {
.main-nav.on::after { /* Le bas du menu raccourcis */
    background-color: #1F7EC0 !important;
}

#header-search, .search-cell { /* Le bandeau de recherche */
    background-color: #1F7EC0 !important;
}

span.search-keyword-label { /*Un texte dans le bandeau de recherche */
    color: #FEFEFC !important;
}

.panel-heading.rappel-fiche { /* Les rappels de fiches dans la recherche de fiches*/
    background-color: #1F7EC0 !important;
}

.collapse.in { /* Les rappels de fiches dans la recherche de fiches*/
    background-color: #0A4D9B !important;
}

#bloc-meta-titre-jeu .navbar-game { /* Les boutons dans les fiches de jeu */
	background-color: #0A4D9B !important;
}

#bloc-meta-titre-jeu, .bloc-fichetop-title { /* Les fiches de jeu */
	background-color: #1F7EC0 !important;
}

.outils-jaquette, .fichejeu-jaquette { /* Les boutons dans les rappels de fiche */
    background-color: #1F7EC0 !important;
}

.nav-primary .nav-lvl1-item { /* Les boutons d'en haut */
    background-color: #1F7EC0 !important;
}

.nav-platform .nav-lvl1-item { /* Les boutons d'en bas */
    background-color: #0A4D9B !important;
}

.nav-lvl2-item, .nav-lvl2-item-repeat, .xXx.nav-link.nav-lv2-link-repeat { /* Les sous menus d'en haut */
    background-color: #1F7EC0 !important;
}

.nav-open { /* Menu en version raccourcie */
    background-color: #1F7EC0 !important;
}

.nav-open-hamburger { /* l'arrière plan du symbole du menu raccouci */
    background-color: #1F7EC0 !important;
}

.nav-open-hamburger::after, .nav-open-hamburger::before { /* La couleur du symbole du menu raccouci */
    background-color: #FEFEFC !important;
}

.nav-lvl2-item a { /* Les interstices et textes du menu déroulant */
    /*background-color: yellow !important;*/
    border-color: #0A4D9B !important;
    color: #FEFEFC !important;
}

.header-top { /* Le bandeau tout en haut */
    background-color: #1F7EC0 !important;
}

.header-sticky, .head-cell-hit { /* Le bandeau arrière du menu en dessous */
    background-color: #0A4D9B !important;
}

.icon-next4::before{ /* La flèche "next" */
    color: #FEFEFC !important;
}

.footer-links { /* Haut du Footer */
    background-color: #1F7EC0 !important;
}

.footer-mentions { /* Bas du footer Footer */
    background-color: #0A4D9B !important;
}

.nav-lvl1-item > a /* Le hover des menus */{
    color: #FEFEFC !important;
}

li.nav-lvl2-item a:hover, .nav-lvl2-item-repeat:hover, .xXx.nav-link.nav-lv2-link-repeat:hover { /* Le hover des sous menus */
    background-color: #FEFEFC !important;
    color: #1F7EC0 !important;
}

.item-lvl2-on > a, .nav-lvl1-item > a:hover, .item-lvl2-on span  { /* Le hover des menus */
    background-color: #FEFEFC !important;
    color: #1F7EC0 !important;
}

.quick-access-bar {
    background-color: #0A4D9B !important;
}

.title {
    background-color: #FEFEFC !important;
    color: #0A4D9B !important;
}
}
