css:
@-moz-document domain("helpdesk.uic.edu") {

body{
    font-family: 'Lato', sans-serif !important;
    font-size: 16px !important;
    background: #EEE !important;
}
#logo{
    display: none !important;
}
#quickbar, #quickbar a{
    color: #FFF !important;
    background: #282C37 !important;
    font-size: 1em !important;
    height: 2em !important;
}
#body {
    margin-left: 18em  !important;
    margin-top: 9.2em !important;
    right: 1em !important;
    border: 0 !important;
    padding: 0 !important;
}
#header h1{
    left: 11.25em  !important;
    padding: 1em !important;
    color: #333 !important;
}
#page-navigation{
    z-index: 0 !important;
}
#page-navigation ul#page-menu{
    top: 5em !important;
    right: 2em !important;
    left: 19em !important;
    background: #282C37 !important;
    border-radius: 4px !important;
    height: 2em !important;
    padding-left: 1em !important;
    line-height: 2em !important;
    color: #FFF  !important;
}
#nav{
    width: 16.5em  !important;
    background: #313541 !important;
    top: 0 !important;
    bottom: 0 !important;
    position: fixed !important;
    border-radius: 0 !important;
    border-left: 0 !important;
    border-top: 0 !important;
}
#system-menu li{
    border: none !important;
    padding-bottom: 1.2em !important;
}
#page-navigation a{
    color: #FFF !important;
    padding: 0 5px;
}
#system-menu li a{
    color: #FFF !important;
    font-size: 1.2em !important;
}
#nav li:first-child {
    margin-top: 5em !important;
}

#topactions{
    top: 5.8em !important;
    right: 2em !important;
}
#topactions select{
    height: 2.8em !important;
    border-radius: 4px !important;
    font-family: Lato !important;
}
#topactions .field{
    height: 2.5em !important;
    border-radius: 4px !important;
    font-family: Lato !important;
    width: 20em !important;
    margin-left: 0.5em !important;
}
.button{
    padding: 8px !important;
    font-family: Lato !important;
    border-radius: 4px !important;
    border: 0 !important;
    font-weight: 400 !important;
    text-shadow: 1px 1px 1px rgba(0,0,0,0.3) !important;
    color: #FFF !important;
    background: #2980b9 !important;
    height: 2.8em !important;
}
#topactions #simple-search .field{
    border: 1px solid #999 !important;
    padding-left: 10px !important;
    height: 2.6em !important;
}
.search-result-views{
    margin-top: -57px !important;
    background: transparent !important;
    border: 0 !important;
    color: #FFF !important;
    padding-right: 1em !important;
}
.search-result-views li a{
    color: #FFF !important;
}
table.ticket-list th.collection-as-table, table.collection th.collection-as-table {
    background: #282C37 !important;
    color: #FFF !important;
    font-size: 1em !important;
    padding-top: 0.7em !important;
    padding-bottom: 0.7em !important;
}
.ticket-list tr th a{
    color: #FFF !important;
}
.ticket-list{
    border: 1px solid #CCC !important;
    border-radius: 4px !important;
    overflow: hidden !important;
    box-shadow: 2px 2px 2px rgba(0,0,0,0.1) !important;
    margin-right: 0 !important;
    padding-right: 0 !important;
}
#footer{
    display: none !important;
}
}
