css:
@-moz-document url-prefix("https://lms.at") {
html {
    background: #171717 url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAIAAAAmkwkpAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAClJREFUeNpiFBQU5OXlZQADJmFhYQYYYPr16xeQ+vz5M4gDEYPIAwQYAJUPBptp0pKhAAAAAElFTkSuQmCC) repeat!important
}
#mainmenu {
    background: #050286!important
}
#mylms {
    border-right: 1px solid #060050!important
}
#favorites {
    border-left: 1px solid #060050!important
}
#favorites .dropdown > span {
    border-left: 1px solid #060050!important
}
.content #content-border {
    background: #050286!important
}
.content #content-shadow {
    background: #212121!important
}
.content #content-shadow {
    border-top: 1px solid #3A3A3A!important
}
.content #content-shadow {
    border-left: 1px solid #3A3A3A!important
}
.content #content-shadow {
    border-right: 1px solid #5F5F5F!important
}
.content #content-shadow {
    border-bottom: 1px solid #5F5F5F!important
}
.portlet-head {
    background-color: #000965!important
}
html {
    color: #F1F1F1!important
}
.portlet-content {
    background: #191919!important
}
.content a,
.content a:visited {
    color: #B3B3B3!important
}
.list-table tr.odd,
.list-tiny tr.odd,
tr.list-odd {
    background-color: #16396B!important
}
.content a.button,
.content a.button:visited {
    background-color: #16396B!important
}
#member-portlet p.h {
    color: #E8E8E8!important
}
#listview {
    border: 1px solid #B1B1B1!important
}
#listview th {
    background-color: #424242!important
}
#listview td {
    background-color: #E0E0E0!important
}
#hd-menu a {
    color: #6671FF!important
}
.dropdown ul {
    border-top-color: #050286!important
}
.dropdown ul {
    border-right-color: #050286!important
}
.dropdown ul {
    border-left-color: #050286!important
}
.dropdown ul {
    border-bottom: 1px solid #050286!important
}
.dropdown>ul>li {
    border-bottom: 1px solid #050286!important
}
#mylms.dropdown>ul>li>a:hover,
#favorites>div.dropdown>ul>li>a:hover,
#location>ul>li.dropdown>ul>li:hover {
    background-color: #35408A!important
}
#mylms:hover,
#location > ul > li > a:hover,
#location .dropdown:hover,
#favorites:hover {
    background: -webkit-gradient(linear, left top, left bottom, from(#050286), to(#4E4C92))
}
.portlet {
    width: 460px!important
}
#doc {
    width: 1050px!important
}
#sidemenu {
    z-index: 0!important
}
#sidemenu {
    display: none!important
}
.content h1,
.content h2,
.content h3 {
    color: #BBB!important
}
#hd-logo {
    background: url(http://puu.sh/pmSQw.png) no-repeat!important
}
.gb_per,
.gb_per_c,
.gb_per_u,
.gb_cat,
.gb_cat_c {
    background-color: #383838!important
}
.gb_ntit,
.gb_n {
    background-color: #737373!important
}
div[style="background:#fff9dd;"] {
    background: #000965!important
}
.gb_total {
    background: #16396B!important
}
#location {
    border-left: 1px solid #060050!important
}
#assignment_tasks .odd {
    background-color: #383838!important
}
.instructions {
    background-color: #737373!important
}
tr.list-header {
    background-color: #000965!important
}
th.list-table,
th.list-table-narrow,
.list-table th,
tr.list-header th {
    border-bottom: 3px solid #16396B!important
}
tr[style="background-color:#EAEAEA;"] {
    background-color: #444444!important
}
a[style="text-decoration:none;color:#000000;background-color:#D5D5D5;"] {
    background-color: #232323!important
}
.sylabus-content-cell {
    background-color: #737373!important
}
.sylabus-section-header-row {
    background-color: #000965!important
}
.sylabus-section-header-row {
    border-bottom: 3px solid #16396B!important
}
.list-table tr.odd {
    background-color: #696969!important
}
.list-table tr.even,
.list-tiny tr.even,
tr.list-even {
    background-color: #424242!important
}
#available_calendars_div {
    background-color: #464646!important
}
th[style="width: 87px;"] {
    background-color: #444444!important
}
table.fc-border-separate {
    background-color: #333333!important
}
.fc-state-highlight {
    background: #888!important
}
#at-a-glance td.selected {
    background-color: #0B0198!important
}
.frontpage #bg {
    background: transparent!important
}
.frontpage #badges {
    display: none!important
}
#badges {
    display: none!important
}
#ft {
    display: none!important
}
}
