css:
@-moz-document url-prefix("http://vk.com/"), url-prefix("https://vk.com/") {
/* ШИРОКАЯ ПАНЕЛЬ СМАЙЛИКОВ */
.emoji_tabs_r_s {
    margin-left: 675px
}
.emoji_tt_wrap, .emoji_list {
    width: 727px
}
.emoji_tabs_wrap {
    width: 650px
}
/* СЧЁТЧИКИ В БОКОВОМ МЕНЮ */
#side_bar li span{position:relative} 
.left_count_wrap,.left_count_wrap:hover { 
font-size: 9px !important; 
top: 0px !important; 
left: 16px !important; 
background: /*[[color]]*/ !important; 
z-index: 2 !important; 
color: white !important; 
border-radius: 12px !important; 
width: 14px !important; 
height: 14px !important; 
padding: 0px !important; 
text-align: center !important; 
position: absolute !important; 
} 
/* СКРЫТЫЕ РЕКОМЕНДАЦИИ */
.right_list, .right_list_header, div#groups_filters_wrap { 
display:none 
}

/* ПОЛЕ ВВОДА */
.im-chat-input .im-chat-input--text {
    min-height:30px;
    width:355px
}
#template_show {
    right: 0px;
    top: 3px;
}
/* КНОПКА ОТПРАВКИ (не работает)
.im-chat-input--textarea>.flat_button {
  visibility: hidden;
}
.im-chat-input--textarea>.flat_button:after {
  width:16px; 
  height:16px;
  content:'?';
  visibility: visible;
  display: block;
  position: absolute;
  background-color: [[sendcolor]];
  border-radius: 90%;
  padding: 5px;
  bottom: 20px;
  left: 28px
} */
/* ГРУППЫ СЕТКОЙ */
.group_list_row {
    border-bottom: 1px solid #eef0f3;
    padding: 15px 5px 15px 5px;
    margin: 0 0 0 0;
    width: 31%;
    height: 200px;
    float: left;
}
.group_row_info {
    overflow: visible;
    color: #65686c;
    padding-top: 2px;
    margin: 90px 0 0 0;
    text-align: center;
}
.group_row_photo {
    float: left;
    margin: 0px 0px 0px 38px;
}
#ui_albums_load_more{
    width: 100%!important;
    float: inherit!important;
}
#ui_groups_load_more{
    float: left;
    width: 550px;}
.group_row_actions .flat_button {
    margin: 10px!important;}
/* НОМЕРА СТРАНИЦ В ОБСУЖДЕНИЯХ */
.pg_flnk_rd {
    border-bottom: 1px solid #ccc;
}
.pg_flnk_sel {
    color: #45688E;
    border-bottom: 4px solid #597da3;
}
#pg_fixed {
    top: 48px;}
.pg_fixed_back {
    top: 1px;
    background: #fff;
    box-shadow: 0 1px 4px 0 #999;
    border-radius: 3px;
    opacity: 1;
    filter: alpha(opacity=100);
    position: absolute;
}
/* РЕКЛАМА */
#ads_left,.ads_ad_box.redesign .ads_ad_box2.ver.repeat_ver.size_site,div[data-ad-view]{
    display: none!important;
}
.ads_ads_box.app_in_news, .ads_ads_box.apps_only, .ads_ads_box.groups_only {
    display: none!important;
}
/* САЙДБАР */
#side_bar_inner,.side_bar_inner {
    position: fixed!important;
    margin-top: 46px!important;
}
}
