css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("m.dilidili.com") {
  
  @media all and (orientation:landscape) {
  
  html, body {
    height: 100%;
    min-height: 100%;
  }
  
  body>div:not(.container) {
    display: none !important;
  }
  
  .container {
    max-width: 99999px;
    height: 100%;
    font-size: 0.24rem;
    
  }
    
    header, .header {
      background-color: transparent !important;
      height: 0;
    }
    
    .header>.logo, .header>.header-so {
      display: none;
    }
  
  #vedio {
    position: fixed;
    bottom: 0;
    left: 0;
    top: 0;
    right: 25%;
  }
  
  #vedio iframe {
    width: 100%;
    height: 100%;
    overflow: hidden;
  }
  
  .episodeWrap, .vedio-info {
    position: fixed;
    left: 75%;
    right: 0;
  }
  
  .episodeWrap {
    bottom: 0;
  }
    
    .episodeWrap li {
      padding: 0.1rem;
    }
    
    .episodeWrap a {
      font-size: 0.48rem;
    }
  
  .vedio-info {
    top: 0;
  }
    
    .vedio-info h1 {
      font-size: 0.48rem;
      line-height: 0.6rem;
    }
    
    .vedio-info p.info {
      font-size: 0.32rem;
      line-height: 0.6rem;
    }

  .info-xxj, .list_fx, .comment, footer, nav {
    display: none !important;
  }

  div[id^="_"] {
      display: none !important;
  }
    
    #menu {
      background-color: transparent !important;
      background: none;
    }
    
    .aside_lef {
      width: 30%;
    }
    
    .aside_lef>.a_closed { display: none !important;}
  
  }
  
}
