body {
    font-size: 100%;
    line-height: 100%;
    background: #242424;
    color: #17b2ff;
}
.titlebar {
    background: #484848;
    color: #17b2ff;
    font-weight: bold;
    clear: both;
    padding: 5px;
    font-family: sans-serif;
}
a {
    color: #17b;
    font-style: oblique;
    text-decoration: none;
    line-height: 200%;
}
a:hover{
    color: #17b2ff;
}
div#cufFooter {
    color: #17b2ff;
    clear: both;
    width: 100%;
    background: #484848;
}
div#cufHeader, table#cufHeader {
    background-color: #484848;
    background-image: -ms-linear-gradient(top, #D58232 0%, #FFBF33 25%, #FFBF33 66%, #C86545 82%, #C86545 83.25%, #C09867 83.25%, #f0e3c1 100%);
    background-image: -moz-linear-gradient(top, #D58232 0%, #FFBF33 25%, #FFBF33 66%, #C86545 82%, #C86545 83.25%, #C09867 83.25%, #f0e3c1 100%);
    background-image: -o-linear-gradient(top, #D58232 0%, #FFBF33 25%, #FFBF33 66%, #C86545 82%, #C86545 83.25%, #C09867 83.25%, #f0e3c1 100%);
    background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0, #17B2FF), color-stop(.25, #242424), color-stop(.66, #484848), color-stop(0.82, #424242), color-stop(.8325, #424242), color-stop(0.8325, #484848), color-stop(1, #17B2FF));
    /* background-image: -webkit-linear-gradient(top, #D58232 0%, #FFBF33 25%, #FFBF33 66%, #C86545 82%, #C86545 83.25%, #C09867 83.25%, #f0e3c1 100%); */
    /* background-image: linear-gradient(#D58232 0%, #FFBF33 25%, #FFBF33 66%, #C86545 82%, #C86545 83.25%, #C09867 83.25%, #f0e3c1 100%); */
}
.chto {
    color: #17b2ff;
    background-color: #242424;
    padding: 0px;
    margin-bottom: 3px;
}
.chte {
    color: #17b2ff;
    background-color: #484848;
    padding: 0px;
    margin-bottom: 3px;
}
.chto a {
    color: #17b2ff;
    font-weight: bold;
    text-decoration: none;
}
.chte a {
    color: #17b2ff;
    font-weight: bold;
    text-decoration: none;
}
textarea {
    width: 85%;
    color: #17b2ff;
    background-color: #484848;
}
div.GameCatalogButton, div.FeaturedGameCatalogButton {
    background-color: #484848;
    border: 2px solid #17b2ff;
    border-radius: 5px;
    cursor: pointer;
    float: left;
    height: 190px;
    line-height: 100%;
    -moz-border-radius: 5px;
    margin: 10px 4px 0 4px;
    position: relative;
    text-align: center;
    width: 223px;
}
div.FeaturedGameCatalogButton {
    background-color: #484848;
    border: 2px solid #17b2ff;
    border-radius: 5px;
    cursor: pointer;
    float: left;
    height: 190px;
    line-height: 100%;
    -moz-border-radius: 5px;
    margin: 10px 4px 0 4px;
    position: relative;
    text-align: center;
    width: 458px;
}
div.FeaturedGameCatalogButton span {
    color: #17b2ff;
    font-size: 19px;
    left: 0;
    line-height: 120%;
    margin-left: 10px;
    position: absolute;
    text-align: left;
    top: 135px;
    width: 444px;
}
div.FeaturedGameCatalogButton span {
    color: #17b2ff;
    font-size: 19px;
    left: 0;
    line-height: 120%;
    margin-left: 10px;
    position: absolute;
    text-align: left;
    top: 135px;
    width: 444px;
}
div.GameCatalogButton span, div.FeaturedGameCatalogButton span {
    color: #17b2ff;
    font-family: HOBO STD,Helvetica,Arial,sans-serif;
    font-size: 21px;
    font-weight: bold;
    line-height: 100%;
    margin-top: 0px;
    text-shadow: 1px 1px #6b3a01;
}
}
