css:
@-moz-document domain("badoo.com") {
.blocker,
.pv_likes,
.footer__item.footer__item--last.js-footer-likes {
    display: none!important;
}
.spotlight {
    display: none;
}
.sidebar-promo {
    display: none;
}
.sidebar-payments {
    display: none;
}
.sidebar-nav .sidebar-nav__btn {
    display: none;
}
#gifts {
    display: none;
}
.js-profile-awards-container {
    display: none;
}
.js-profile-verify-container {
    display: none;
}
.st_normal .messenger-banner-wrap,
.st_msgs_disable .messenger-banner-wrap {
    display: none;
}
.messenger {
    width: 860px;
    height: calc(100% - 300px);
}
.page_ {
    width: 1560px;
}
.page-right-side,
.page-right-footer {
    width: 1290px;
}
.photo-list {
    width: 1290px;
}
.profile-info-v2__left {
    width: 990px;
}
.with-side-footer .page-right-footer {
    display: none;
}
#app_s,
#app_c {
    margin-bottom: 50px;
}
.messenger-ovl_,
.messenger-ovl-fade {
    margin-left: -518px;
}
.messenger {
    width: 1010px;
}
.section-chat-right {
    width: 730px;
}
.message p {
    font-size: 16px;
}
.section-header--folder ~ .banner-container--wide {
    display: none;
}
.messenger-ovl-fade {
    background: #eee;
    opacity: 1;
}
}
