css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(animeseason.com) {
    
    html, body{
        background: #282828 !important;
        
        font-size: 16px !important;
        line-height: 26px !important;
    }
    
    tr{
        height: 2.5rem;
    }
    
    /* CONTAINER */
    div#container{
        width: 72rem;
    }
    
    /* HEADER */
    div#header {
        height: 8.3rem;
        margin-top: 3rem;
        padding: 0;
        background: url('https://i.imgur.com/sHeUsz1.png');
        background-repeat: no-repeat;
        background-color: #282828;
        background-size: 20%;
        background-position: 0 2.3rem;
    }
    div#home_link a br:nth-of-type(1),
    div#home_link a br:nth-of-type(2),
    div#home_link a br:nth-of-type(3){
        display: none;
    }
    div#home_link {
        width: 15rem;
        height: auto;
        margin-top: 1rem;
    }
    
    #mr_bar,
    #feature_menu_wrapper{
        display: none;
    }
    
    
    /* MAIN */
    div#main{
        background: #282828 !important;
        width: unset;
    }
    div#left_top,
    div#right_top,
    div#content_top{
        background: none !important;
        height: 0;
    }
    
    
    
    
    /* --- LEFT COLUMN /* -> RIGHT ------------------------------------------------------- */
    div#left_col{
        /* float: right !important; */
        width: 17rem;
    }
    div#left_menu{
        position: relative;
        padding: 0rem;
        background-image: none !important;
    }
    
    
    
    
    /* ONGOING */
    div#header_ongoing{
        margin-bottom: 0rem;
        padding: 0.5rem 1rem; 
        background-image: none !important;
        background-color: #383838;
    }
    div#header_ongoing:before {
        content:'ONGOING SERIES';
        color: #cdcdcd;
        font-weight: 600;
    }
    
    /* ONGOING SERIES */
    div#left_menu ul.series:nth-of-type(2) li a{
        /* important first letter */
        display: block;
        
        padding: 0.4rem 1rem;
        margin: 0;
        
        font-size: inherit;
        background-color: #3c3c3c;
        border: none;
        border-bottom: 0.1rem solid #282828;
        
        transition: all 0.2s;
    }
    div#left_menu ul.series:nth-of-type(2) li a:first-letter{
        font-weight: 700;
        /* color: #acacac; */
    }
    
    
    
    
    
    /* FULL SERIES MENU */
    div#header_animelist{ display: none; }
    div#left_menu ul.series:first-of-type{
        position: absolute;
        top: -6rem;
        left: 17rem;
        width: 55rem;
        /*left: -58.5rem;*/
        /*width: 61.1rem;*/
        
        
        font-weight: 600;
        font-size: 1.2rem;
        text-align: center;
        
    }
    div#left_menu ul.series:first-of-type li{
        display: inline;
        float: left;
    }
    div#left_menu ul.series:first-of-type li a{
        padding: 0.5rem 1.081rem 0.1rem;
        margin: 0;
        
        font-size: inherit;
        background-color: inherit;
        border: none;
        border-bottom: 0.2rem solid #3c3c3c;
        
        transition: all 0.2s;
    }
    div#left_menu ul.series:first-of-type li a:hover{
        border-bottom-color: #298ecc;
    }
    div#left_menu ul.series:first-of-type li a{
        font-size: 0.01rem;
    }
    div#left_menu ul.series:first-of-type li a:before,
    div#left_menu ul.series:first-of-type li a:after{
        font-family: FontAwesome;
        font-size: 1.2rem;
    }
    div#left_menu ul.series:first-of-type li:nth-of-type(1) a:before{ content: '\f00b\0020'; }
    div#left_menu ul.series:first-of-type li:nth-of-type(1) a:after{ content: '\0020 Anime List'; }
    
    div#left_menu ul.series:first-of-type li:nth-of-type(2) a:before{ content: '\f017\0020'; }
    div#left_menu ul.series:first-of-type li:nth-of-type(2) a:after{ content: '\0020 Recent Animes'; }
    
    div#left_menu ul.series:first-of-type li:nth-of-type(3) a:before{ content: '\f201\0020'; }
    div#left_menu ul.series:first-of-type li:nth-of-type(3) a:after{ content: '\0020 Top Rated'; }
    
    div#left_menu ul.series:first-of-type li:nth-of-type(4) a:before{ content: '\f02c\0020'; }
    div#left_menu ul.series:first-of-type li:nth-of-type(4) a:after{ content: '\0020 Categories'; }
    
    div#left_menu ul.series:first-of-type li:nth-of-type(5) a:before{ content: '\f074\0020'; }
    div#left_menu ul.series:first-of-type li:nth-of-type(5) a:after{ content: '\0020 Random Anime'; }
    
    
    
    
    
    
    
    /* --- RIGHT COLUMN: CONTENT ------------------------------------------------ */
    
    div#right_col{
        width: 55rem;
    }
    
    
    /* Headline */
    .header_bloc{
        margin-bottom: 0;
        padding: 0.5rem 1rem !important; 
        
        width: unset !important;
        height: unset !important;
        line-height: inherit !important;
        
        background: #383838 !important;
    }
    /* Style the first one different than the uninteresting rest */
    div#content_wrapper .header_bloc:first-of-type {background: #cdcdcd !important;}
    .header_bloc:before {
        /* content:'MAYBE AN ICON HERE?'; */
        color: #3c3c3c;
        font-weight: 600;
    }
    .header_bloc h1,
    .header_bloc h3{
        font-size: 1rem;
        font-weight: 600;
        color: #282828;
        text-transform: uppercase;
    }
    
    
    
    
    
    
    
    
    
    /* CONTENT: START*/
    #frontpage_left_col {
        width: 100%;
        margin: 0;
    }
    
    /* Headlines */
    h2.frontpage_header {
        height: unset;
        margin: 1.5rem 0 0.5rem;
        padding-left: 0;
        background-color: inherit;
        border: none;
        
        color: #CC923F;
        line-height: inherit;
        font-size: inherit;
        font-weight: 600;
    }
    #frontpage_left_col > h2{
        margin-top: 0.5rem;
    }
    
    
    /* Series Blocks */
    .series_block{
        float: left;
        width: 10.5rem;
        height: 11rem !important;
        /* height: unset !important; */
        margin: 0.5rem;
        padding: 0.8rem;
        overflow: hidden;
        background-color: #383838;
        border-bottom: 0.1rem solid #282828;
    }
    p.series_block{
        clear: both !important;
        width: auto !important;
        height: unset !important;
    }
    
    /* Thumbnails */
    #frontpage_left_col img.thumb_padding {
        width: 5rem;
        padding-bottom: 1rem;
    }
    /* Title */
    #frontpage_left_col h2.title {
        font-size: inherit;
        padding: 0;
        float: left;
        width: 100%;
    }   
    #frontpage_left_col h3,
    #frontpage_left_col p{
        float: left;
        width: 100%;
    } 
    
    
    
    
    /* Take the full width of the content container */
    #frontpage_right_col {
        width: 100%;
        margin: 0;
    }
    /* Clear to get position of heading right */
    #frontpage_right_col h2 {
        clear: both;
    }
    /* Spacing after clear*/
    #frontpage_right_col h2.frontpage_header:not(:first-of-type){
        padding-top: 1.5rem;
    }
    /*#frontpage_right_col .series_blocks a img.thumb_padding {
        width: auto !important;
        height: auto !important;
    }*/
    
    
    
    
    
    
    
    
    /* CONTENT: SINGLE VIEW */
    
    div#content_wrapper{
        width: 100%;
        background: none !important;
    }
    
    div#content{
        padding: 0;
        margin-left: 0.7rem;
    }
    
    div.content_bloc{
        margin: 0;
        margin-bottom: 0.7rem;
        background-color: #3c3c3c;
        border: none !important;
        padding: 1rem 0.8rem;
    }
    
    img#poster{
        border: none;
        margin: 0.3rem 1rem 1rem 0rem;
    }
    
    /* Rating */
    div#avg_rating{
        width: unset;
        height: unset;
        padding: 0.65rem 1rem;
        font-weight: 600;
    }
    div#rating_term{
        height: unset;   
    }
    div#rating_set{
        margin: 0;
        padding: 0 0 0.5rem;
    }
    div#ratings p{
        margin-top: 0.5rem;
    }
    
    
    
    
    /* Must Watch Listing */
    .header_bloc h3{
        color: #cdcdcd;
    }
    div#must_watch_listing{
        border: none;
        float: none;
        width: 100%;
        line-height: inherit;
    }
    #must_watch_listing dl.must_watch_pair{
        float: none;
        border: none;
        border-bottom: 0.1rem solid #282828;
    }
    div#must_watch_listing dt {
        border: none;
    }
    div#must_watch_listing .must_watch_count {
        border: none;
    }    
    div#must_watch_listing .must_watch_video {
        float: none;
        width: unset;
        background-color: #383838;
        border: none;
    }
    div#must_watch_listing .must_watch_count,
    div#must_watch_listing .must_watch_video{
        padding: 0.3rem 1rem;
    }
    #must_watch_listing .must_watch_video a{
        padding: 0.3rem 1rem;
    }
    
    
    
    
    
    
    
    /* SINGLE VIEW: VIDEO */
    div#video_update_bar {
        padding: 0.5rem 1rem;
        background-color: #383838;
        margin-bottom: 1rem;
    }
    
    /* Return to index - stupid non responsive hack */
    div.video_update_col {
        width: 33%;
    }
    
    div#navigate {
        margin: 0;
    }
    #nav_next, #nav_prev {
        width: auto;
    }
    /* Next and Previous Video */
    #nav_next a, #nav_prev a {
        border: none !important;
        background-color: #282828;
        padding: 0.3rem 1rem;
        font-weight: 600;
        font-size: 1rem;
        color: #cdcdcd;
        margin-top: 1rem;
    }
    
    
    
    
    
    
    
    /* --- FULL LIST ----------------------------------------------------- */
    /* LETTERS */
    ul#alpha_listing li {
       float: left;
       margin: 0;
       /*padding: 0.3rem 1rem;*/
       border: none;
       /*width: 2rem;*/
       text-align: center;
    }
    ul#alpha_listing li a {
        width: 2rem;
        padding: 0.5rem 1rem;
    }
    
    
    /* List columns */
    div.series_col {
        width: 25.72rem;
    }
    
    /* List column headline */
    h2.series_alpha {
        background-color: #383838;
        padding: 0.3rem 1rem 0.5rem;
        color: #cdcdcd;
    }
    div.series_alpha {
        border-top: 0.3rem solid #CC923F;
        margin: 1rem 0;
        background-color: #383838;
    }
    
    ul.series_alpha {
        margin: 0;
    }
    /* List icons */
    ul.series_alpha li{
        list-style-image: none;
        border-bottom: 0.1rem solid #282828;
        padding: 0.3rem 1rem;
    }
    ul.series_alpha li:before{
        content: '‚ûù';
        padding-right: 1rem;
    }
    
    
    
    
    
    
    
    
    /* LIST FILTER */
    ul.category_col {
        width: 33%;
    }
    .btn_submit {
        background-color: #383838;
        border: none;
        border-bottom: 0.2rem solid #282828;
        color: #cdcdcd;
        padding: 1rem 1.5rem;
        font-weight: 600;
        font-size: 1rem;
        width: 100%;
        cursor: pointer;
    }
    
    
    
    
    
    
    /* FOOTER */
    div#footer{
        height: 1rem;
        padding: 1rem 1.5rem;
        font-weight: 700;
        color: #fc2;
        background: #282828 !important;
    }
    
    
}
