css:
@-moz-document domain(jeuxvideo.com) {

/****************
     Principal
****************/  

/* Suppression des machine */
.header-bottom, .header-sticky {
   height: 0px !important;
}
   
/* Largeur menu */
.header-top .header-container {
    width: 980px !important;
} 
   
/* Logo qui passe par dessus le reste pour qu'il sois cliquable */
.logo-link {
    position: relative !important;
    z-index: 100000 !important;;
}

/****************
     MENU
****************/

/* Suppression menu machine */
.nav-container.nav-platform {
   display:none !important;
}
   
/* Décalement menu vers la gauche */
.nav-lvl1{
    margin-left: 90px !important;
}
   
/* Ajustement taille de police dans le menu */
.nav-primary .nav-lvl1-item > .nav-link {
    font-size: 16px  !important;
}
   
/* Suppresion bouton store */
.nav-primary .nav-lvl1-item:last-child {
   display: none !important;
}
   
/* Ajustement hauteur de ligne dans le menu */
.nav-primary .nav-lvl1-item > .nav-link {
    line-height: 47px !important;
}

/****************
     POSTS
****************/

/* Remise en place de la date */
  .bloc-message-forum .bloc-header {
    display: flex !important;
  }
  
  .bloc-message-forum .bloc-header .bloc-date-msg {
    order: 2 !important;
    position: unset !important;
    margin-top: 12px !important;
    margin-left: auto !important;
    flex: 1 !important;
    text-align: right !important;
  }
  
  .bloc-message-forum .bloc-header .bloc-pseudo-msg {
    order: 0 !important;
  }
  
  .bloc-message-forum .bloc-header .bloc-mp-pseudo {
    margin-top: 1px !important;
    order: 1 !important;
  }
  
  .bloc-message-forum .bloc-header .bloc-options-msg {
    order: 3 !important;
    margin-left: auto !important;
  }

/* Remise en place de l'avatar */
  .bloc-message-forum .bloc-avatar-msg {
    display: block !important;
  }
  
  .bloc-message-forum .back-img-msg {
    width: 66px !important;
    padding: 2px !important;
  }
  
  .bloc-message-forum .user-avatar-msg {
    max-width: 60px !important;
    max-height: 60px !important;
  }
  
  .bloc-message-forum .bloc-header {
    margin-left: 78px !important;
    height: 40px;
  }
  
  .bloc-message-forum .bloc-pseudo-msg {
    margin-left: 0px !important;
    padding-left: 0px !important;
  }
  
  .bloc-message-forum .bloc-contenu {
    padding-left: 78px !important;
  }
  
  .bloc-message-forum .bloc-date-msg {
    left: 90px !important;
  }

/****************
     BLOCS
****************/

/* Style des blocs à droite */
    .panel-jv-forum .titre-info-fofo {
      text-transform: uppercase;
      color: #FFF;
    }

	.panel-jv-forum {
     background: #002f46;
    }
    .panel-jv-forum>.panel-heading {
     background-color: rgba(0,67,101,0.75);
    }
	.panel-jv-forum .titre-info-fofo-link {
     color: #FFF;
    }
	.panel-jv-forum .bloc-info-forum .lien-jv, .panel-jv-forum .bloc-forums-preferes .lien-jv, .panel-jv-forum .bloc-sous-forums .lien-jv {
     color: rgba(255,255,255,0.85);
	}

/****************
     FOOTER
****************/

/* Suppression du bloc de pub */
.footer-links {
	display:none;
}

}
