css:
@-moz-document url-prefix("http://tree.tv/") {
/*Set main page__________________________________________________________________________________________*/

body {	
    background: #1d2129 !important;
    color: #ccc;
}
#makeMeScrollable_top,
.main_bg, .top_menu.fixed,
.search.fixed {
    background: #2d313a !important;    
}

.top_menu.fixed, .search.fixed {    
    border-left: none;
    border-right: none;
}
.search.fixed {    
    box-shadow: black 0 12px 30px -5px;
    border-bottom: none; 
}
body > span > style + div {
 	height: 0 !important;   
}
.traforet-br-logo {
 	display: none !important;   
}

/*search area*/
.header .search .search_main input[type='text'] {
    background-color: #4b4f56 !important;
    border: none !important;
    color: #eee !important;
    box-shadow: 0 0 0 0 #ccc inset !important;
}
.dropsearch span.middle {    
    color: #eee !important;
}
.search_global .search_global_bg {
    background-color: #262c3a;
}

/*log_on_user_panel*/
.header .top_reg {
    background: none; 
}
.reg_links a {
    font-size: 12px;
    font-weight: bold;
}
.reg_links a:first-child {
    margin: 15px 0 0;
}
.reg_ava > a > img {
    margin: 12px 0 0 5px;
    max-width: 60px;
    max-height: 60px;
}

/*shadow-off_on_the_carusel*/
.popular_content,
.main_content .item,
.main_content_open .item {
    background: none !important;
}

/*button_filter-options*/
.center_menu .left_menu a.prosmotr,
.center_menu .left_menu a.rait,
.center_menu .left_menu a.new,
.center_menu .left_menu a {
    background: #4b4f56;
    color: #eee !important;
}
.center_menu .left_menu a:hover,
.center_menu .left_menu a.new:hover,
.center_menu .left_menu a.new.active {
    background: url("http://tree.tv/imgs/new_button_main.png") no-repeat scroll left bottom transparent !important;  
}
.center_menu .left_menu a.prosmotr:hover,
.center_menu .left_menu a.prosmotr.active,
.center_menu .left_menu a.rait:hover,
.center_menu .left_menu a.rait.active {
    background: url("http://tree.tv/imgs/button_profile.png") no-repeat scroll right bottom !important;
}
.a_filter_year {
    background: #4b4f56;
    color: #eee;
}
.a_filter_year:hover,
.a_filter_year.active {
    background: url("http://tree.tv/imgs/new_button_main.png") no-repeat scroll -173px bottom !important;
}
.production > a {
    background: #4b4f56;
    color: #eee;
}
.production > a:hover,
.production > a.active {
    background: url("http://tree.tv/imgs/button_profile.png") no-repeat scroll left bottom rgba(0, 0, 0, 0);    
}

/*content_main_page*/
.main_content_item > .item {
    box-shadow: black 0px 0px 5px 0px;
    padding-bottom: 0;
}
.main_content_item > .item:hover {
    top: -2px;
    right: -1px;
    box-shadow: #3aca26 0px 0px 5px 0px;
}
.main_content .item .item_content,
.main_content .item .item_content h2 {
    background: #4b4f56 !important;
}
.item_content h2 a {
    font-weight: bold;
    color: #eee !important;
}
.item_name_ico, .del_wrap {
    background: #4b4f56;   
    border-radius: 0 0 0 10px;
    padding: 4px 7px 7px 7px;
}
.item_name_text {
    background: #4b4f56;
    color: #eee;
}
.smoll_janr {
    border-radius: 0 10px 0 0;
    background: #686988;
    border-bottom: none;
}
.smoll_janr a {
    color: #eee;
    font-size: 12px;
    vertical-align: top;
}
.smoll_year {
    border-radius: 10px 0 0;
}

.main_content_open .item .item_content {
    background-color: #2d313a;    
}

/*scroll up*/
#left_up {
    transition: all 0.2s;
    width: 85px;
}
#left_up:hover {    
    background: rgba(0, 0, 0, 0.52);
}
.left_up_content {
    background: none;
    color: rgba(255, 255, 255, 0);
    transform: rotate(-90deg);
    margin: 115px -61px;
    width: 205px;
}
.left_up_content:after {
    content: ">>>";
    color: rgba(95, 95, 95, 0.29);
    font-size: 30px;
    font-weight: bold;
}

/*set_color_number_of_page*/
.paginationControl a.page {
 	color: #eee !important;   
}
#give_more > a {
    background: #4b4f56;
    color: #eee;
}
#give_more > a:hover {
    background: #3c3e42;    
}

/*set_blocks_filters*/
.banners_block_content .scroll-pane {
    box-shadow: 0 0 1px 1px #4dae46;
}
.jspPane > a {
    color: #ccc;
}
.jspPane > a:hover {
    background-color: #EEEEEE;
    color: #333;
}
.jspPane > a.active {
    background-color: #5ed600 !important;
    color: #262c3a;
}
.jspTrack {
    background: #262c3a;
}
.reset_janrs > a, .apply_q > a {
    background: #4b4f56;
    color: #ccc;
    box-shadow: 0 0 1px 1px #4dae46;    
    line-height: 31px;
}
.reset_janrs > a:hover, .apply_q > a:hover {
background: #43464c;
}

/*set_block_new_comments*/
.content_top_collection > ul,
.content_top_collection > .comments_wrap_bg {
    background: url("http://tree.tv/imgs/comments_pattern.png") no-repeat scroll center bottom #262c3a !important;
    margin-top: -11px !important;
}
.content_top_collection > ul {
 	margin-bottom: 15px;   
}
.banners_block_content,
.comments_wrap_bg {
    background: url("http://tree.tv/imgs/comments_pattern.png") no-repeat scroll center bottom #262c3a !important;
    margin-top: -6px !important;
}
.paginationControl.comment_paging {
    background: #262c3a;    
}
.right_comment_item_text,
.right_comment_item_user a {
    color: #ccc;
}

/*content_alternate_view*/
.main_content_open .item.open {   
    background: none !important;
    box-shadow: black 0px 0px 15px 0px;
    padding-bottom: 0;
}
.main_content_item.open .item_open_content > h2 a {
    color: #ff5371 !important
}
.main_content_item.open .item_open_content > h2 a:hover {
    color: #58B545 !important;
}
.main_content_item.open .item_open_content {
    color: #eee !important;
}


/*set_inner_content ___________________________________________________________________________________________*/

/*color_navigation_text*/
.breabcrumbs_wrap span,
.content_open .item .item_open_content {
    color: #ccc;
}

/*poster_block*/
.content_open .item .preview { 
    color: #339c00;
    background: #4b4f56;
    padding: 10px;
    margin-bottom: 10px;
    /*box-shadow: #222 0px 2px 5px 0px;*/
}

/*button-play_under_the_poster-block*/
.film_play_button > a {
    background: url(http://tree.tv/imgs/button_play_1.png) 301px -4px no-repeat scroll #4b4f56;
    height: 40px;
    line-height: 36px;
    border: 1px rgba(202, 99, 117, 0.41) solid;
    color: #4b4f56;
}
.film_play_button > a:after {
    content: "PLAY";
    color: #ccc;
    display: block;
    margin-top: -34px;
}
.film_play_button > a:hover:after {
    content: "PLAY";
    color: #ca6375;
    display: block;
    margin-top: -34px;
}
.film_play_button > a:hover {
    background: url("http://tree.tv/imgs/button_play_1.png") 301px -50px no-repeat scroll #474a50;
    box-shadow: #ca6375 0 0 5px;
    color: #474a50;
}

/*style_description_content_text*/
.content_open .item .item_open_content .description {    
    line-height: 20px;
    text-indent: 1em;
}

/*shadow-off_voice-block*/
.votes_wrap {    
    background: none;
}

/*set_voice-block*/
.content_open .item .item_right .more_actions .raiting .votes {    
    background-color: #2d313a;    
    box-shadow: none;
    color: #ccc;
    height: 65px;
    width: 170px;
}

/*buttons_similar-content*/
.film_tabs .left_film_tabs a {
    background: #4b4f56;
    color: #ccc;
    font-weight: bold;
}
.film_tabs .left_film_tabs a:hover,
.film_tabs .left_film_tabs a.active {
    background: url("http://tree.tv/imgs/new_button_main.png") no-repeat scroll left bottom;
}

/*trailer-button*/
.film_tabs .buttons.film .trailer {
    font-weight: bold;
    text-indent: 15px;
    padding-right: 55px;    
    background: url("http://tree.tv/imgs/trailer.png") no-repeat scroll 96px 0px #4b4f56;
    color: #ccc;    
    width: 80px;
    border-right: 1px #4b4f56 solid;
}
.film_tabs .buttons.film .trailer:hover {       
    background: url("http://tree.tv/imgs/trailer.png") no-repeat scroll 96px -37px #4b4f56;        
}

/*block_files*/
.main_content_item .accordion .accordion_item .accordion_head {
    background: #4b4f56;
    padding-left: 5px;
    width: 715px;
    border-bottom: 1px #2d313a solid;
}
.main_content_item .accordion .accordion_item .accordion_head a > span {
    width: 45px;
}
.main_content_item .accordion .accordion_item .accordion_head a.active > span[data-quality="480"] {
 	background-position: 10px -48px !important;   
}
.main_content_item .accordion .accordion_item .accordion_head > a {   
    padding-left: 45px !important;    
    width: 515px !important;
}
.accordion_head > .smoll {    
    margin-right: -10px;
}
.accordion_head .download_load a {
 	background-position: 11px 10px !important;
}
.accordion_head .download_load a:hover {
    background: url("http://tree.tv/imgs/download_mini.png") no-repeat scroll transparent !important;
 	background-position: 11px 11px !important;
}
.accordion_head > .smoll > .smoll,
.accordion_head > .smoll > .smoll:hover {
    background: url("http://tree.tv/imgs/arr_one.png") no-repeat scroll 52px center #4b4f56;    
    box-shadow: none !important;
    color: #ccc;
} 
.main_content_item .accordion {
    background: #242b31;
    padding: 5px 0 5px 0;
    width: 730px;
}
.main_content_item .accordion .accordion_item {
    margin-bottom: 0;
    padding: 0 5px 0 5px;
}
.main_content_item .accordion .accordion_item .accordion_head > a,
.main_content_item .accordion .accordion_item .accordion_content .accordion_content_item .film_title .file_title {
 	color: #ccc !important;   
}
.main_content_item .accordion .accordion_item .accordion_content .accordion_content_item .film_title .file_title:hover {
    color: #48B721 !important;
}
.main_content_item .accordion .accordion_item .accordion_content .accordion_content_item .film_actions .watch_mini {
    background: url("http://tree.tv/imgs/new_play.png") 111px 0 no-repeat scroll #4b4f56;
    border: 1px rgba(202, 99, 117, 0.41) solid;
    color: #4b4f56;
    padding-right: 30px;
    margin: 3px 0 0 -35px;
    text-indent: 0px;
}
.main_content_item .accordion .accordion_item .accordion_content .accordion_content_item .film_actions .watch_mini:hover {
    background: url("http://tree.tv/imgs/new_play.png") 111px -32px no-repeat scroll #3d4146;
    box-shadow: #ca6375 0 0 3px;
    color: #3d4146 !important;
}
.main_content_item .accordion .accordion_item .accordion_content .accordion_content_item .film_actions .watch_mini:after {
    content: "PLAY";
    color: #ccc;
    display: block;
    margin-top: -30px;
}
.main_content_item .accordion .accordion_item .accordion_content .accordion_content_item .film_actions .watch_mini:hover:after {
    content: "PLAY";
    color: #ca6375;
    display: block;
    margin-top: -30px;
}
.film_actions .file_size,
.main_content_item .accordion .accordion_item,
.main_content .main_content_item .add_comment {    
    color: #ccc !important;
}
.film_actions .file_size {
    padding-right: 2px;
}
.main_content_item .accordion .accordion_item .accordion_content .accordion_content_item .film_title {
    width: 560px;
}
.main_content_item .accordion .accordion_item .accordion_content .accordion_content_item:nth-of-type(odd) {
    background-color: #292c33;
}
.main_content_item .accordion .accordion_item .accordion_content .accordion_content_item:nth-of-type(even) {
    background-color: #2d3038;
}

/*remove_two_lines_under_the_files-block*/
.main_content_item.un-height hr {
    display: none;
}

/*feedback_write_block"*/
.add_comment .gray_btn {
    background: #2d313a;
    color: #cccccc;
    border: #5f5f5f 1px solid;
    box-shadow: none;
}
.add_comment .gray_btn:hover {
background: #2d313a;
    border: #777 1px solid;
}
.emoji-wysiwyg-editor {
    background: #2d313a;
    max-width: 725px;
    border: #5f5f5f 1px solid !important;
    box-shadow: none; 
}
.emoji-wysiwyg-editor:focus {
    box-shadow: 0 0 5px rgba(59, 179, 33, 0.5);
}

/*comments_bloks*/
.main_content .main_content_item .comments_wrap .comment .left img {
    max-width: 80px;
    max-height: 107px;    
    /*border-radius: 3px;*/
}
.main_content .main_content_item .comments_wrap .comment .left {   
    margin-bottom: -2px;
	margin-right: 0px;
}
.main_content .main_content_item .comments_wrap .comment .right {
    margin-left: 90px;
}
.main_content .main_content_item .comments_wrap .comment .left span.nick_name {   
    position: absolute;
    left: 90px;
}
.main_content .main_content_item .comments_wrap .comment {
    background: #2d313a;
    color: #ccc;
    border: 1px #5f5f5f solid;
    box-shadow: none;
    padding: 5px;
}
.main_content .main_content_item .comments_wrap .comment .right .right_date .right_nickname {
    color: #3BB321;    
}
.main_content .main_content_item .comments_wrap .comment .right .right_date .right_nickname:hover {
    color: #3BB321;
    text-decoration: underline;
}
.main_content .main_content_item .comments_wrap .comment .right .right_date {   
    font-size: 10px;
    font-weight: bold;
    padding-bottom: 10px;
}
.news_wrap .news .likes,
.main_content .main_content_item .comments_wrap .comment .right .right_date .likes {  
    font-weight: bold;
    font-size: 12px;
    color: #999;    
    right: 0px;
    top: -12px;
}
.right_date {
    left: -5px;
    top: 15px;
    position: absolute;
    color: #999;
}
.right > p > img,
.right_comment_item_text > img,
.emoji-wysiwyg-editor img {
 	margin: 0 2px !important;
    padding: 2px;
    background: #fdf7eb;
    border-radius: 2px;
}
.right > p {
    line-height: 20px;
    text-indent: 1em;
    display: block;
    min-height: 54px !important;
    background-color: #2d313a;
    margin-left: -5px;
    padding: 3px 7px 20px;
}
.main_content .main_content_item .comments_wrap .comment .right .answer {
    padding: 0;
    position: absolute;
    bottom: 7px;
}
.prem_profile_link {
    right: 5px;
    bottom: 5px;
    font-size: 11px;
}

/*font_color_to_any_place_on_the_site*/
.no_trace,
.quality_item.both,
.quality_desc > a,
.awards p,
.new_films h2 a.title,
.profile_tabs + div > p,
.profile_tabs > div > h1,
.top_col_list > span {
 	color: #ccc !important;   
}
a {
    color: #ccc;
}
.comments_wrap h1 {    
    color: #ccc;
}

/*pop-up_video-block*/
.popup {
    background: rgba(28, 34, 41, 0.75) !important;
    padding: 10px;
    margin-left: -523.5px !important;
}
#fancybox-outer,
#fancybox-title-over {
    background: rgba(28, 34, 41, 0.75) !important;
}

/*pop-up_screenshots-block*/
#fancybox-close {
    right: 10px !important;
    top: 10px !important;
}
#fancybox-close {
    background: none !important;
}
#fancybox-close:after {
    content: "+";
    transform: rotate(45deg);
    font-size: 50px;
    font-weight: bold;    
    display: block;
    text-align: center;
    line-height: 30px;
    color: #555;
}

/*profile_sets*/
.left_profile_tabs a {
    background: #1d2129 !important;
    border-right: none;
    color: #cccccc !important;
    font-weight: bold !important;
}
.left_profile_tabs a.active {
    background: url("http://tree.tv/imgs/button_profile.png") no-repeat scroll left bottom transparent !important;
}
.left_profile_tabs a.profile_tab_Recom.active {
    background: url("http://tree.tv/imgs/button_profile.png") no-repeat scroll right bottom transparent !important;
}

.profile_info_wrap {
    background: #2d313a;
}
.profile_info_bg,
.profile_foto {
    background: #40454e;
}
.profile_foto_shadow > .gray_btn {   
    background: #1d2129 !important;
    border: none;
    box-shadow: none;
    color: #ccc;
    font-weight: bold;
    line-height: 30px;    
}
.people_tabs li a,
.recomend_head_tabs a {    
    color: #ccc;    
}
.people_tabs li a:hover,
.people_tabs li a.active,
.recomend_head_tabs a:hover {    
    color: #53B643;    
}
.search_people input[type="text"] {
    border: none;
    box-shadow: none;
    background: #40454e;
    color: #ccc;
}
.show_people span {
    background: url("http://tree.tv/imgs/arr_one.png") no-repeat scroll 52px center #40454e;
    box-shadow: none;
    color: #ccc !important;
}
span.middle {
    background: url("http://tree.tv/imgs/arr_one.png") no-repeat scroll 122px center #40454e;
    box-shadow: none;
    color: #ccc;
}
.show_people:hover span,
.choose_wrap_item:hover span.middle {
    box-shadow: 0px 0px 1px 1px #5ed600;
}
.people_selection {
    background: #40454e;
    border: #5f5f5f 1px solid;
    width: 693px;
}
.people_selection a,
.people_list a {
	color: #ccc !important;   
}
.people_list {
    background: none repeat scroll 0 0 #2d313a;
    border: 1px solid #5f5f5f;
    border-top: none !important;  
    width: 679px;
}
.people_list:nth-child(2n) {
    background: #40454e;
}

.news_wrap .news {
    box-shadow: none; 
}
.news {
    background: #1d2129;
}


/*REMOVE_ADDS________________________________________________________________________________________*/

/*remove_pushdown-block*/
.wrapper {
    margin-top: 0px !important;
}
#ads_detect,
.registr_info {
 	position: fixed !important;
    top: -1000px !important;
}
div [id*='admixer_'],
div [id*='admixer_async_'],
div [id*='epom-'] {
	display: none !important;
}
body > div:first-child {
 	position: absolute !important;   
    top: 10px !important;
}

/*remove_side-stickers*/
#feedback_tab,
#android_app_link,
.profileplus_link,
.commercial_link {
    display: none !important;
}

/*remove_social_blocks*/
#vk_groups, 
#vk_groups_2,
.fb-page,
.fb_iframe_widget {
    display: none !important;    
}

.new_back_link {
    width: 0%;
}

noindex > div {
 	display: none !important;   
}

.banners_block.no_back {
    display: none !important;
}
 
}
