css:
@-moz-document url-prefix("https://web.whatsapp.com/") {


/* vars */
:root {
--light-bg: #1D1E2A !important;       			/* light bg */
--med-bg: #1A1B26 !important;      				/* secondary bg */    
--dark-bg: #14151F !important;        			/* dark bg */
--main-text: #777777 !important;      			/* white text */
--second-text: #556B82 !important; 				/* blue text */
--third-text: #74857C !important;   			/* green text */

--main-font: Open Sans !important; 				/* main font */

--bg-image: url("http://puu.sh/nT7DJ/52480dbe86.jpg"); 	/* background image */
}   

/* background */

body{
	background-color: var(--dark-bg);
	background-image: none !important;
	color: var(--main-text);
}

#startup, #initial_startup {
	background-color: var(--dark-bg);
}

.backdrop-transparent{
	background-color: var(--dark-bg) !important;	
}

.app-wrapper{
	background-image: var(--bg-image);
}

.btn-plain:hover, .app-wrapper-web .app, .incoming-msgs, .btn-more, #window{
	box-shadow: none !important;
}

/* chat background style                                                                */
/* the default background is a solid color,                                             */
/* feel free to change to any of the other two options by removing the comments,        */
/* the second options makes the background transparent and the third inserts an image,  */
/* which you can change to a different one if you'd like                                */

.app{
	background-color: var(--light-bg);
/*	background: transparent !important;*/
/*	background-image: url("http://i.imgur.com/9y8BXCa.jpg");*/
}

.app-wrapper-web .app{
	border-radius: 0px !important;
}

/* connection popup */

.popup{
	background-color: var(--light-bg);
	box-shadow: none !important;
}

.popup hr{
	border-top: none;
}

.popup-title{
	color: var(--second-text);
}

.popup-contents, .controls-container{
	color: var(--third-text);
}

.popup .text-tip a, .popup .text-tip{
	color: var(--main-text);
}

.btn-plain{
	color: var(--second-text);
}

.btn-plain:hover{
	color: var(--third-text);
}

.btn-default{
	color: #ffffff !important;
	background-color: var(--second-text);
}

.btn-default:hover{
	color: #ffffff !important;
	background-color: var(--third-text);
}

/* sidebar background */
body{
	font-family: var(--main-font);
}
  
.pane-list-user{
	background-color: var(--dark-bg);
}  
  
.bubble-actions, .menu-horizontal, .menu-horizontal > span, .menu-tabs, .menu-pills, .audio, .drawer-header-offset, .media-panel-header, .list-action, .media-collection{
	background-color: var(--dark-bg);
}

.pane-header, .pane-subheader{
	background-color: var(--dark-bg);
}

.pane-list-header{
	background-color: var(--dark-bg);
}
  
.subheader-search{
	background-color: var(--med-bg);	
}  

.input-search{
	background-color: var(--med-bg);	
}
  
.cont-input-search{
	background-color: var(--med-bg);	
	border: 0px;
}  

.subheader-search::after{
	background-color: var(--med-bg);
}

.chatlist .chat.active, .chatlist .active.location{
	background-color: var(--dark-bg);	
}  

.chatlist .chat:hover, .chatlist .location{
	background-color: var(--light-bg);	
}

.chatlist .chat, .chatlist .location{
	background-color: var(--med-bg);		
}

.chatlist .chat.active::after{
	background-color: var(--dark-bg);
	border-bottom-color: var(--dark-bg);
}

.chatlist .chat.hover::after{
	background-color: var(--dark-bg);
	border-bottom: 0px;	
}

.chatlist .chat::after{
	background-color: var(--dark-bg);	
}

.column, .chatlist, .pane-list-body, .table{
	background-color: var(--med-bg);	
}

.chatlist .chat-body, .chatlist .header-body{
	border-top: 0px;
}

.chatlist .chat:hover, .chatlist .location:hover, .chatlist .chat.hover, .chatlist .hover.location{
	background-color: var(--dark-bg);
}

.avatar, .avatar-image{
	border-radius: 50%;
}

/* sidebar text */

.chat-title .emojitext, .location-main .emojitext, .header-title .emojitext{
	font-family: var(--main-font);
	font-weight: 600;
	color: var(--second-text);
}

.ellipsify, .starred-title-main{
	color: var(--main-text);	
}

.chat-meta .chat-time{
	color: var(--main-text);	
}

/* chat bg */

/*.app .pane-two{
		background-color: var(--light-bg);	
}*/

.pane-intro{
	background-color: var(--med-bg);
	border-left: 0px;
}

.intro-image{
	background-image: url("http://i.imgur.com/DKbS2YW.png");
}

.intro-title{
	color: var(--second-text);
}

.pane-intro::after{
	border-top: none !important;
}

.empty-text, .intro-text, .hint{
	color: var(--third-text);
}

.pane-chat{
	background: transparent !important;
/*	background-color: var(--med-bg);*/
}

.pane-chat-header{
	background-color: var(--dark-bg);
	border-left: 0px;
}

.pane-chat-tile-container{
	background: transparent !important;
/*	background-color: var(--dark-bg);*/
}

.pane-preview{
	background: var(--light-bg);
}

.pane-chat-header::after, .subheader-search::after{
	background-color: var(--dark-bg);
	border-bottom: 0px;
}

.message-list{
	background: transparent !important;
/*	background-color: var(--light-bg);*/
}

.block-compose{
	background-color: var(--dark-bg);
}

.block-compose .input-container{
	background-color: var(--dark-bg);
	border: 0px;
}

.message-in{
	background-color: var(--dark-bg);
	box-shadow: none;
	border-radius: 10px 
}

.message-in::before{
    background-image: none;;
}

.message .message-text .emojitext{
	color: var(--main-text);
	font-family: var(--main-font);
}

.message-author{
	font-family: var(--main-font);
	font-weight: 600;
}

.message-out{
	background-color: var(--dark-bg);
	box-shadow: none;
	border-radius: 10px 
}

.message-out::before{
	background-image: none;
}

.message-datetime{
	color: var(--main-text);
}

.pane-chat-footer{
	background-color: var(--dark-bg);
	border-left: 0px;
}

.context{
	background: none !important;
}

.context-icon{
	/*background-image: url("http://i.imgur.com/i505eVy.png") !important;*/
	filter: invert(100%);
}


.message-system-body{
	color: var(--second-text);
	background-color: var(--dark-bg);
	text-shadow: none !important;
	box-shadow: none !important;
	border-radius: 5px;
}

.pane-chat-body{
	border-left: 0px;
}

/* info pane */

.pane-chat-tile{
	background-image: none !important;
}

.drawer-header-small{
	background-color: var(--dark-bg);
}

.drawer-title-body{
	color: var(--main-text);
}

.drawer-body-msg-info{
	background-color: var(--light-bg);
}

.well{
	background-color: var(--med-bg);
}

.drawer .chatlist{
	background-color: var(--light-bg);
}

.title-underlined{
	border-bottom: 0px;
}

.drawer-section-title{
	color: var(--second-text);
}

.chat-secondary, .header-secondary{
	color: var(--main-text);
}

.drawer-section-footer.separator{
	border-top: 0px;
}

.drawer-section-footer{
	color: var(--main-text);
}

.drawer-body, .drawer-section{
	background-color: var(--med-bg);
}

.header-title{
	color: var(--second-text);
	font-family: var(--main-font);
	font-weight: 600;
}

.location-secondary, .header-secondary{
	color: var(--third-text);
}

.list-action{
	color: var(--main-text);
	border-bottom: 0px;
}

.list-action:hover{
	background-color: var(--light-bg);
}

/* right click dropdown */

.dropdown{
}

.dropdown .menu-item a{
	background-color: var(--dark-bg);
	color: var(--main-text);
	font-family: var(--main-font);
}

.dropdown, .dropdown-picker{
	background-color: var(--dark-bg);
}

/* misc */

.drawer-header{
	background-color: var(--dark-bg);
}

.drawer-starred .drawer-body{
	background-color: var(--med-bg);	
}

.drawer-settings .drawer-body{
	background-color: var(--med-bg);		
}

.chatlist.list-settings .first .chat-avatar, .chatlist.list-settings .first .location-icon{
	border-top: 0px;
}

.chatlist.list-icons .chat:hover::after{
	border-bottom: 0px;
}

.avatar-overlay{
	background-color: var(--dark-bg);
	color: var(--main-text);
}

.input-advanced{
	border-bottom: 0px;
}

.input-advanced.active{
	border-color: var(--light-bg);
}

.placeholder{
	color: var(--main-text);
}

/*.emoji-panel{
	background: var(--light-bg);
	background-color: var(--light-bg);
}*/

.icon{
	filter: invert(100%);
}

.msg-unread{
	background-color: var(--light-bg);
	border: none;
}

.msg-unread-body{
	color: var(--main-text);
	background-color: var(--dark-bg);
	border: none;
}

.incoming-msgs{
	background-color: var(--dark-bg);
}

.drawer-media .drawer-body{
		background-color: var(--light-bg);
	}

.btn-fill{
		color: var(--second-text);
		background-color: var(--light-bg);
	}

.media-thumb.active .media-thumb-body{
		border: 3px solid var(--second-text);
	}

.btn{
  background-color: var(--third-text);
	}	

.link-preview-body{
	background-color: var(--dark-bg);

}

.link-preview-title{
	color: var(--second-text);
}

.link-preview-description{
	color: var(--third-text);
}

.link-preview-source{
	color: var(--main-text);
}

.message-in .link-preview-container{
	background-color: var(--dark-bg);
}

.link-preview-container{
	border-radius: 0px;
}

.message-text a, .contents a{
	color: var(--second-text);
}

.chat, .location{
	background-color: var(--dark-bg);
}

.media-viewer{
	background-color: var(--light-bg);
}

.media-content .btn-round{
	background-color: var(--dark-bg);
	box-shadow: none;
}
	
.media-chat .chat-main, .media-chat .header-main{
	color: var(--second-text);
}

.message-in .audio-state-played .audio-progress{
	background-color: var(--second-text) !important;
}

.message-in .audio-state-played .audio-track::-moz-range-thumb{
	background-color: var(--second-text) !important;
}

.message-in .audio-track::-moz-range-track{
	background-color: var(--light-bg);
}

.pane-chat-body.selectable{
	background-color: var(--light-bg);
}

.multi-controls{
	background-color: var(--dark-bg);
}

.selected .msg-select{
	background-color: rgba(20,21,31,.45);
	background-blend-mode: normal;
}

.checkbox.checked{
	background-color: var(--third-text);
	border: solid 2px var(--third-text);
}

.checkbox{
	border-color: var(--third-text);
}

.msg-select:hover, .selected .msg-select:hover{
	background-color: rgba(20,21,31,.45);
}

.list-title{
	background-color: var(--med-bg);
	color: var(--second-text);
}

.subheader-search.active{
	box-shadow: none;
}

.subheader-search.active .cont-input-search{
	border: none;
}

.chatlist .chat:hover::after{
	border: none;
}

/*	#window{
		background-color: var(--dark-bg);
	}*/

#platforms{
	background-color: var(--med-bg);
}
	
.icon-platform{
	background-color: var(--main-text)
}

.qr-button{
	background-color: var(--second-text)
}

.entry-title{
	color: var(--second-text)
}

.entry-subtitle{
	color: var(--main-text)
}

.entry-text{
	color: var(--main-text)
}

.hint{
	color: var(--main-text)
}

.icon{
	filter: invert(100%) !important;
}

#wrapper::before{
	background-color: transparent;
}

.drawer{
	background: var(--dark-bg);
}

.chat:hover:not(.active), .location:hover:not(.active){
	background: var(--med-bg);
}

.unread-count{
	background-color: var(--second-text);
	color: var(--dark-bg);
}

.tooltip{
	background-color: var(--dark-bg);
	color: var(--main-text);
}

.chatlist-panel-body{
	background-color: var(--med-bg);
}

.message-out .tail-container, .message-out .tail-container.highlight, .message-in .tail-container.highlight, .tail.message-in .tail-container{
	background-image: none !important;
}

.drawer-section.well-simple-body{
	background-color: var(--dark-bg);
}

}
