css:
@-moz-document domain("teksyndicate.com") {
.main-container .row-fluid {
    background: #000000;
    box-shadow: none;
}

a, a:link, a:visited {
    color: #a21e1e;
    text-decoration: none;
}

.feed-cn {
    color: #757272;
}

p {
    color: #6d6969;
}

.content-summary {
    color: #a21e1e;
}

li {
    color: #a21e1e;
}

strong {
    color: #6d6969;
}

#reply-bar {
    background-color: black;
}

article.post .cooked {
    background-color: #000000;
}

article.post blockquote {
    background-color: #272626;
    }
}
