css:
@-moz-document domain("thejimquisition.com") {
/*Thank God for Jim Fucking Sterling Son.*/

.well {
    background-color: #1d1d1d;
    border-color: #505050;
}
#secondary .widget > h3 {
    color: #efefef;
}
.widget ul li {
    border-color: #505050;
}
body {
    color: #ffefef;
    background-color: #1D1d1d;
}
.post-inner-content {
    background-color: #1d1d1d;
    border-color: #505050;
}
.entry-title a {
    color: #efefef;
}
.entry-content {
    color: #ffefef;
}
.entry-meta a {
    color: #dfdfdf;
}
.tagcloud a {
    background-color: #0a0000;
}
#disqus_thread {
    background-color: #1d1d1d;
    border-color: #505050;
}
.post-navigation a,
.page-numbers,
.pagination .prev,
.pagination .next {
    border-color: #505050;
    background-color: #1d1d1d;
}
.site-main [class*="navigation"] a,
.more-link,
.pagination>li>a,
.pagination>li>span {
    color: #DA4453;
}
body.archive .post-inner-content,
body.blog .post-inner-content,
.post-inner-content:first-child {
    border-color: #505050;
}
.form-control {
    background-color: #303030;
}
input[type="text"],
input[type="email"],
input[type="tel"],
input[type="url"],
input[type="password"],
input[type="search"],
textarea {
    color: #999;
    border-color: #505050;
}
input[type="text"]:focus,
input[type="email"]:focus,
input[type="tel"]:focus,
input[type="url"]:focus,
input[type="password"]:focus,
input[type="search"]:focus,
textarea:focus {
    color: #efefef;
}
/*New Comments Section 2017*/

#respond,
#comments,
.wpdiscuz-subscribe-bar,
#wpcomm .wc-comment .wc-comment-right,
#wpcomm .wc-reply .wc-comment-right {
    background-color: #262626;
}
.wpdiscuz_select {
    background: #1d1d1d!important;
    border-color: #505050!important;
}
#wpcomm select,
#wpcomm input[type="text"],
#wpcomm input[type="email"],
#wpcomm input[type="url"],
#wpcomm input[type="date"],
#wpcomm input[type="color"],
#wpcomm .wpdiscuz-subscribe-form-button input[type="submit"],
#wpcomm .wc-comment-footer,
#wpcomm,
#wpcomm .wc-comment-title,
.wpdiscuz-subscribe-bar,
#wpcomm .by-wpdiscuz {
    border-color: #505050;
}
#wpcomm .wc-form-wrapper,
#wpcomm .wc-field-textarea textarea,
#wpcomm .wpd-form-row .wpd-field,
#wpcomm .wpdiscuz-textarea-wrap {
    border-color: #505050;
    background-color: #1d1d1d;
}
#wpcomm .wc-blog-guest>.wc-comment-right .wc-comment-author,
#wpcomm .wc-blog-guest>.wc-comment-right .wc-comment-author a {
    color: #fff;
}
#wpcomm .wc-comment-text {
    color: #ffefef;
}
#wpcomm select,
#wpcomm input[type="text"],
#wpcomm input[type="email"],
#wpcomm input[type="url"],
#wpcomm input[type="date"],
#wpcomm input[type="color"],
#wpcomm .wpdiscuz-subscribe-form-email input[type="email"] {
    background-color: #1d1d1d;
}
#wpcomm .page-numbers {
    color: #fff;
    background-color: #1d1d1d;
}
.copyright:before {
    font-size: 9px;
    color: #666;
    content: "Dark Style by CheatFreak ";
}
}
