css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("support.nextlimit.com") {

  
  html, body,#rw_wrapper,#rw_wrapper .wiki-content,.rw_dashboard{
       background-repeat: repeat !important;
    background-color: #181818 !important;
}
  
  
  #rw_page_right,.rw_pagetree, #rw_spaces_macro_container,.pagebody {
    border-bottom: 1px solid rgba(255,255,255,.18) !important;
    border-radius: 15px !important;
    box-shadow: inset 0px 1px 5px 0px #000 !important;
    border-top-color: #000 !important;
    border-right-color: #111 !important;
    border-left-color: #111 !important;
    background-color: #141414 !important;
  color: #fff !important;
    font-weight: bold !important;
  }
  .aui-message.success,.aui-message.hint{
    box-shadow:  0px 1px 5px 0px #000 !important;
    border-top-color: #000 !important;
    border-bottom: 1px solid rgba(255,255,255,.18) !important;
    border-right-color: #111 !important;
    border-left-color: #111 !important;
    background-color: rgb(60,60,60);
    color: #fff !important;
  }
  .aui-message.warning,.rw_page_left_section,.aui-message.problem{
    box-shadow:  0px 1px 5px 0px #000 !important;
    border-top-color: #000 !important;
    border-radius: 5px !important;
    border-bottom: 1px solid rgba(255,255,255,.18) !important;
    border-right-color: #111 !important;
    border-left-color: #111 !important;
    background-color: rgb(60,60,60);
    color: #fff !important;
  }
   
  .rw_pagetree_search,.rw_primary{
    border-bottom: 1px solid rgba(255,255,255,.18) !important;
    border-radius: 15px !important;
    box-shadow: inset 0px 1px 5px 0px #000 !important;
    border-top-color: #000 !important;
    border-right-color: #111 !important;
    border-left-color: #111 !important;
    background-color: #141414 !important;
    color: white !important;
    font-weight: bold !important;
  }
  h1, h2, h3, h4, h5, h6, .wiki-content h1, .wiki-content h2, .wiki-content h3, .wiki-content h4, .wiki-content h5, .wiki-content h6, .pagetitle, #title-text, #title-text a, .steptitle, .substeptitle, .formtitle, a.blogHeading, .more-comments a, .label, label, th.confluenceTh, table.confluenceTable th.confluenceTh, table.admin th, .form-element-large, .form-element-small, #com-atlassian-confluence .mce_h1 span.mceText, #com-atlassian-confluence .mce_h2 span.mceText, #com-atlassian-confluence .mce_h3 span.mceText, #com-atlassian-confluence .mce_h4 span.mceText, #com-atlassian-confluence .mce_h5 span.mceText, #com-atlassian-confluence .mce_h6 span.mceText,h1, h2, h3, h4, .wiki-content h1, .wiki-content h2, .wiki-content h3, .wiki-content h4, .pagetitle, .steptitle, .substeptitle, .formtitle, table.confluenceTable td.confluenceTd.highlight, table.confluenceTable td.confluenceTd.highlight > p, table.confluenceTable th.confluenceTh, table.confluenceTable th.confluenceTh > p, .wiki-content .tablesorter-header-inner, table.admin th, .form-element-large, .form-element-small, #toolbar #format-dropdown .format-h1 a, #toolbar #format-dropdown .format-h2 a, #toolbar #format-dropdown .format-h3 a, #toolbar #format-dropdown .format-h4 a, u{
    color: orange;
  }
  .wiki-content a, .wiki-content a:link, .wiki-content a:visited, .wiki-content a:focus, .wiki-content a:hover, .wiki-content a:active, confluenceTd{
    color: orangered;
  }
  span, p{
    color: lightgrey !important;
  }
  
}
