css:
@-moz-document domain(0chan.eu) {
.content-background, .logo, span.plus, body, html, h2 {
  color:#698CC0 !important;
  background-color: #181818 !important;
}
span.plus {
  border: none;
}
.newssub {
  background-color: #282828 !important;
}
.reply, #overlay_menu, .replymode, body h2, body h1 {
  background-color: #282828 !important;
  border: solid 1px #575757 !important;
  border-radius: 0px;
}
a {
  color: #C9BE89 !important;
}
a:hover {
  color: #EEFEBB !important;
}
.filetitle {
  color: #E1E2CF !important;
}
.postername {
  color: #C0E9F0
}
.captchaimage.content-background {
  background-color: white !important;
}
.postblock, .de-file-img {
  color: #698CC0 !important;
}
input[type="text"], textarea
{
  background-color:#111111 !important;
  border: solid 1px #575757 !important;
  color:#698Cff !important;
}
ul li {
  color:#698CC0 !important;
  background-color: #181818 !important;
}
}
