css:
@-moz-document domain(originaltrilogy.com){

 /* maybe adjust width*/
  #top > main:nth-child(2){
    width:100%!important;
    max-width:90%!important;
  }
 
  
  .breadcrumbs, .summary{
    padding:0px!important;
  }
  
  .summary > h1, .breadcrumbs > ul > li{
    padding-bottom:0px!important;
    bottom:10px!important;
  }
  
  .breadcrumbs > ul *{
    padding-left:1.5pc!important;
  }
  
  .announcements > h2, .category > h2{
    padding:1pc!important;
  }
  
    section.category:nth-child(1) > h2:nth-child(1) {
    padding:1pc!important;
  }
  
 .description, .category > article > p, article > ul{
    padding-top:0pc!important;
    padding-bottom:0pc!important;
    margin-top:0px!important;    
  }
  
  .discussion *{
    position:static!important;
    margin-top:0px!important;
    margin-bottom:0px!important;
  }
  
  .discussion > .stats, .discussion > .last-post{
    position:relative!important;
    top:1pc!important;
    padding-bottom:6px!important; /*might redo this later */
  }
  
  .discussion > .description{
    position: relative!important;
    top:6px!important;
  }

  
   .category > article, .announcements > article {
    padding-top:1pc!important;
    padding-bottom:1pc!important;
    font-size: 1.3rem!important;
  }
  
  .announcements > article{
    padding-top: 1pc!important;
  }
  .to-the-top{
    padding:0px!important;
    margin-top:1pc!important;
    margin-bottom:0px!important;

  }
  
  /* to change announcements to be smaller */
  .announcements > .topic{
    padding: 0px!important;
  }
  /* end announcements */

  
  /*BEYOND THE MAIN FORUM CATEGORIES MENU*/
  
  .notice, .locked{
    padding-top:1em!important;
    padding-bottom:1em!important;
    padding-right:0px!important;
  }
  
  .private:before{
  position: absolute!important;
  top:.2em!important;}

  .locked:before{
    position: absolute!important;
    top:.15em!important;
  }
  
  .topics > h2{
    display: none!important;
  }
  
   .actions > ul:nth-child(1) {
    margin:0px!important;
    padding-top:5px!important;
    padding-bottom:5px!important;
    padding-left: 0px!important;
    padding-right: 0px!important;
    background: #424757!important;     
  }
  
  
  /*first for general topics, second for private*/
  .topics > .topic, main > .topic{
    padding:0px!important;
    min-height: 0px!important;
  }
  
  .pagination {
    padding-top:.35pc!important;
    padding-bottom:.35pc!important;
    min-height: 0px!important;
  }
  
  .pagination > ul{
    position:relative!important;
  }
  
  .pagination *{
    top:0rem!important;
  }
  .topic *{
    min-height: 0pc!important;
    padding: 0pc;
    font-size: 1.4rem
  }
  .topic > h3{
    padding-top:.3pc!important;
  }
  .topic > .avatar{
   top:4px!important;
    max-height: 72px!important;
    max-width: 72px!important;
  }
  
  
  .details {
    line-height: .8pc!important;
    position: relative!important;
    bottom:5px!important; /*used to be 4*/
  }

  dd.replies{
line-height: 1em!important;  
  } /* make number of replies not spill over */
  
  /*adjusts "last post" position*/
   dt.last-post, dd.last-post{
    padding-right:30em!important;
top:3em!important;  
    }
  
  
  
  /*----------------indiv topics--------------*/
  
  .signature{
    padding-top:.25pc!important;
    padding-bottom: .25pc!important;
  }
  
  article > .edited > a{
    position:relative!important;
   top:-1pc!important;  
  }
  
  .signature > p{
    margin:.2pc!important;
  }
  
  header > .avatar{
    max-width: 72px!important;
    max-height: 72px!important;
    top:1.5px!important;
  }

  header > dl {
    position:relative!important;
    padding-bottom:0px!important;
    top:-1pc!important;
    
  }
  .post > header{
    padding:0px!important;
    min-height: 0px!important;
  }
  
  article > .content{
   margin-bottom:1rem!important;
   margin-top: 1rem!important;
   font-size: 1.4rem!important;
  }
  blockquote{
    margin:0px!important;
    padding: 2rem!important;
    border-width: 4px!important;
  }
  /* for nested quotes in signatures*/
  .signature > blockquote{ /* the alpha here is not precise, may wish to tweak later */
    background-color:rgba(55, 59, 73, .7)!important;
    border-width: 4px!important;
    border-color: rgb(55, 59, 73)!important;
    border-bottom-style: solid!important;
  }
  
  blockquote > p > a{
    position:relative!important;
    padding-top:1rem!important;
    padding-bottom: 1rem!important;
    top:1pc!important;
  }

  /* to remove gradient in new topic*/
  .topics > .actions::before, .topics > .actions::after{
    display: none!important;
  }
}
