css:
@-moz-document domain("putlockers.ch") {
body {
    background-image: url(https://imgur.com/3cn84xs.png);
}
td.mainlogo a {
    height: 90px;
    background-repeat: no-repeat;
    display: inline-block;
    background-image: url(https://i.imgur.com/naP0rKg.png);
}
td.mainlogo img {
    visibility: hidden;
}
td[style="background-color:#C05083;color:#FFFFFF;width:12px;height:12px;padding:1px;padding-left:2px;padding-right:2px;border:#999999 1px solid"] {
	background-color: #068ddc !important;
    color: #FFFFFF;
    width: 16px;
    height: none;
    padding: none;
    padding-left: none;
    padding-right: none;
    border: #068ddc 1px solid !important;
    border-radius: 2px;
}
td[style="width:12px;height:12px;padding:1px;padding-left:2px;padding-right:2px;border:#999999 1px solid"] {
	background-color: #23a8f5;
    color: #23a8f5;
    width: 16px;
    height: none;
    padding: 5px !important;
    padding-left: none;
    padding-right: none;
    border: #23a8f5 1px solid !important;
    border-radius: 2px;
}
td[style="width:12px;height:12px;padding:1px;padding-left:2px;padding-right:2px;border:#999999 1px solid"]:hover {
	background-color: #068ddc;
	animation: .3s all ease-in-out;
	cursor: pointer !important;
	border: #068ddc 1px solid !important;
}
a[style="color:#000000;text-decoration:none;"] {
	color: #fff !important;
}
.content-box {
    border: 1px solid #101010;
    background-color: #101010;
    width: 1100px;
}
img[style="padding:1px;border:1px solid silver;"] {
	border: 1px solid #101010 !important;
	width: 175px;
}
A:active, A:link, A:visited {
    text-decoration: none;
    color: #fff;
}
div[style="width:136px; padding-top: 5px; color:red"] {
    background-color: #23a8f5;
    border-radius: 2px;
    height: 19px;
    width: 177px !important;
}
.menu-box {
    display: none;
}
.footer-box {
    background-color: #23a8f5;
    border: 1px solid #23a8f5;
    font-family: Arial;
    font-size: 12px;
    text-align: left;
    padding: 5px;
    color: #fff;
    border-radius: 2px;
    width: 900px;
}
font[color="green"] {
	color: #fff !important;
}
h1 {
    border-bottom: 1px solid #23a8f5;
    background-color: #23a8f5;
    border-radius: 2px;
}
h2[style="color:black;margin-bottom:0px !important;border-top:1px solid silver;padding:5px;background-color:beige;color:green;"] {
	display: none;
}
#nav {
    width: 950px;
    height: 33px;
    background: none;
    border-radius: 0;
    display: inline-block;
    margin: 0;
    padding: 0px 0px 0px 0px;
    background-color: #23a8f5;
}
#nav .dropdown .col1 ul li a:hover, #nav .dropdown2 .col1 ul li a:hover, #nav>li a:hover, #nav>li.active>a {
    background: none;
    color: #ffffff;
    background-color: #068ddc;
}
td.mainlogo {
    background-color: #101010;
    border: 1px solid #101010;
}
input[style="border:1px solid silver;height:25px;width:90px;font-size:11px;padding:2px;background-color:beige;"] {
	border: 1px solid #23a8f5 !important;
    background-color: #23a8f5 !important;
    color: #fff !important;
    border-radius: 2px;
}
input[style="border:1px solid silver;height:25px;width:90px;font-size:11px;padding:2px;background-color:beige;"]:hover {
	cursor: pointer !important;
}
input[style="border:1px solid silver;height:16px;width:266px;font-size:11px;padding:4px 1px 3px 1px;color:blue;background-color:beige;"] {
    border: 2px solid #23a8f5 !important;
    height: 16px;
    width: 266px;
    font-size: 11px;
    padding: 4px 1px 3px 1px;
    color: white !important;
    background-color: #101010 !important;
    border-radius: 2px !important;
}
#nav .dropdown {
	background-color: #068ddc;
    border: #068ddc solid 1px;
}
#nav .dropdown .col1 ul li a, #nav .dropdown2 .col1 ul li a {
    color: #fff;
}
#nav .dropdown2 {
	background-color: #068ddc;
    border: #068ddc solid 1px;
}
img[style="padding:1px;border:1px solid silver;"]:hover {
    opacity: 0.7;
}
font[color="red"] {
    color: #fff;
    background-color: #cf000f;
    border-radius: 3px;
    font-size: 16px;
    padding: 2px;
}
h2[style="color:green;margin-bottom:0px !important;border-top:1px solid silver;padding:5px;background-color:beige;"] {
    display: none;
}
td, strong {
    color: #23a8f5;
}
a.movgr {
    display: none;
}
.entry {
    border-bottom: 2px solid #23a8f5;
    color: #cccccc;
    text-align: left;
    padding: 5px;
}
.entry2 {
    border-bottom: 2px solid #23a8f5;
    padding: 5px;
}
h2, strong {
    color: #fff !important;
}
h2[style="color:green;margin-bottom:0px !important;margin-top:0px !important;padding:5px;background-color:beige;"] {
    background: #101010 !important;
}
h2 {
    border-bottom: 2px solid #23a8f5;
}
h2[style="color:black;margin-bottom:0px !important;margin-top:0px !important;padding:5px;background-color:beige;color:green;"] {
    background-color: #23a8f5 !important;
    border-radius: 2px;
}
h2[style="color:black;margin-bottom:0px !important;padding:5px;background-color:beige;color:green;"] {
    background-color: #23a8f5 !important;
	border-radius: 2px;
}
h2[style="color:gray;margin-bottom:0px !important;margin-top:0px !important;padding:5px;background-color:beige;color:green;"] {
    background-color: #23a8f5 !important;
	border-radius: 2px;
}
.form_info {
    color: #f1e4e4;
    font-weight: 700;
}
input.submit_form_textfield {
    border: 2px solid #23a8f5 !important;
    height: 16px;
    width: 266px;
    font-size: 11px;
    padding: 4px 1px 3px 1px;
    color: white !important;
    background-color: #101010 !important;
    border-radius: 2px !important;
}
.submit_form_button {
    border: 1px solid #23a8f5;
    padding: 5px 10px;
    font-weight: 700;
    margin: 3px 0 5px;
    background: #23a8f5;
    color: #fff;
    border-radius: 2px;
}
.submit_form_button:hover {
    background: #068ddc;
    cursor: pointer !important;
}
/* Scrollbar */
 
::-webkit-scrollbar{width: 10px;}
 
::-webkit-scrollbar-thumb{background-color:rgb(35, 168, 245); border-radius: 0;}
::-webkit-scrollbar-thumb:hover{background-color:rgb(35, 168, 245);}
 
::-webkit-scrollbar-track{background-color:rgb(0, 0, 0);}
 
 
/* End Scrollbar */
}
