css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(www.velkyzpevnik.cz) {
.chord {
	color: /*[[colorLabel]]*/;
    font-weight:bold;
    font-size:15px;
}
#reklama {display:none !important;}
body {
    background-color: #222;
}

p, .norm, .bottom-box {
    color: #777;
}
.bottom-box a {
    color:/*[[colorLabel]]*/;   
}
.bottom-box a:hover {
    color:#888;   
}

input, textarea, select {
    background-color: #333;
    border: 1px solid #333;
    height: 25px;
    color: #888 !important;
}

input.a2, input.a1{
	background-color:/*[[colorLabel]]*/;
    border:1px solid #111;
  	color:#000 !important;
}
#menu-box{
    margin-top:6px;
}
.menu-item {
	background-color: /*[[colorLabel]]*/;
    color: white;
    height:25px;
    border-left: 1px solid #111;
    border-bottom: 1px solid #111;
	border-top: 1px solid #111;
    padding: 5px 10px;
}
.off {
    color: #444;
    text-decoration:none;
}
.menu-item A:visited {
    color: #111;
    text-decoration: none;
}
.menu-item A:link {
    color: #111;
    text-decoration: none;
}
.menu-item A {
    color: #111;
    text-decoration: none;
}
.menu-item a:hover{
	color:#000;
}

.autor {
	text-align:left;
}

.pisen, .nazev  {
	color: #666;
}
.nazev {
	    border-bottom: 1px solid /*[[colorLabel]]*/;
}
.song, .song b a, li.kat a b{
	color: /*[[colorLabel]]*/;
	font-size: 13px;
}


.song, .song b a:hover, li.kat a b:hover, li.kat a:hover{
    color: #888;
}

.song a:hover{
    color:#888;
}
.kat a:hover{
    color:#888;
}

.diskuze_telo, .fieldset{
	display:none;
}

.zarazka, .transpozicni-box {
	background-color: #333;
    color: #666;
}

.col-kat {
    border-left: 1px solid #333;
}

div#chordbox {
    background: #333 !important;
}
div#chordbox pre {
    color: #888
}
div#chordbox pre font {
    color: /*[[colorLabel]]*/
}

#search-box{
border: 1px solid #333;
}
}
