css:
@-moz-document url-prefix("https://artuna.planfix.ru"){
.main-menu .main-menu-head::after {
    background: #535C69 !important;
    box-shadow: 0 0 11px 1px #535C69;
    display: none;
}
.main-menu {
    background: #535C69 !important;
}
.main-menu .main-menu-head {
    background: #535C69 !important;
    width: 180px;
    border-bottom: 0px solid #fff !important
}
.main-menu-logo {
    width: 170px;
    height: 37px;
    background: url(http://scr.artuna.ru/qQF/logo.png) no-repeat;
    margin: 4px 0 0 15px;
}
.main-menu .main-menu-tail::after {
    border-bottom: 0px solid #fff !important
}
.planner-block-ajax {
    display: none;
    margin-left: 10px;
}
.cardmenu {
    background: #EAEBED;
}
.leftmenu-topblock {
    background: #EAEBED;
}
.left-panel-resize {
    background: #EAEBED;
}
.cardmenu table .active .link {
    margin-right: -8px;
    background: #AEB6BD;
}
.cardmenu table .active .edge div {
    background: #EAEBED;
}
.planfixcardmenu {
    background: #EAEBED;
}
.planfixcardmenu .leftmenu-topblock,
.docs-menu .leftmenu-topblock {
    background: #EAEBED;
}
.planfixcardmenu + .left-panel-resize .leftmenu-topblock,
.docs-menu + .left-panel-resize .leftmenu-topblock {
    background: #EAEBED;
    border-bottom: 1px solid #ccc;
}
.b-ddl-menu-selected {
    font-size: 13px!important;
}

.planner-block-cards .planner-block-list-wrapper {
    background-color: #EAEBED;
}

.planner-block-list {
    background: #EAEBED;  
    border-top-right-radius: 15px;
}

.dl-task-microcard-container {
    position: relative;
    padding: 10px 10px 10px;
    background: #fff;
    border: 0px solid #dfdfdf;
    border-radius: 0px;
    box-shadow: 0 1px 1px rgba(48, 59, 77, 0.1), 0 1px 0 rgba(48, 59, 77, 0.1);
}

.planner-block-resizer {
    width: 10px;
    visibility: hidden;
    cursor: e-resize;
    height: 35px;
    margin: 0px;
}
.planner-list-quick-add {
    border: 0px;
    box-shadow: 0 1px 1px rgba(48, 59, 77, 0.1), 0 1px 0 rgba(48, 59, 77, 0.1);
}


.planner-drag.planner-drag-microcard {
    padding: 10px;
    border-radius: 0px;
    background: #fff;
}
.planner-page-new-block {
    box-shadow: 0 1px 1px rgba(48, 59, 77, 0.1), 0 1px 0 rgba(48, 59, 77, 0.1);
    background: #fff;
}
.planner-page-new-block-header {
    font-size: 14px;
}
.third-panel-resize {
    background: #EAEBED;
}
.dl-task-microcard-container.task-card-opened {
    border: 4px solid #6FA92E;
    background: #fff;
}
input.btn.ui-button {
    color: #fff;
    border: 0px;
    border-radius: 20px;
    background: #6FA92E;
    font-size: 14px;
    height: auto;
    padding: 7px 10px;
}
input.btn.ui-button:hover {
    color: #fff;
    border: 0px;
    border-radius: 20px;
    background: #535C69;
    font-size: 14px;
    height: auto;
    padding: 7px 10px;
}
.tr-group-header {
    background: #EAEBED;
    color: #fff;
}
.tbl-list .tr-group-header>td,
.tbl-tool-list .tr-group-header>td {
    border-top: none;
    padding: 10px 10px 10px;
    font-weight: bold;
    font-size: 12pt;
}
.active a {
    font-weight: bold
}
.tbl-list .tr-group-header>td a,
.tbl-tool-list .tr-group-header>td a {
    color: #535C69;
    text-decoration: none;
}
.b-ddl-menu .b-ddl-menu-selected-arrow input.btn {
    color: #fff;
}
.b-right-block.b-last-block[data-type=taskdata] {
    box-shadow: none;
    background: #EAEBED;
}
.left-panel-resize.collapsed {
    background: #EAEBED;
}
.leftmenu-container.hidden {
    background-color: #EAEBED;
}
.b-right-panel ul li.right-panel-item-active {
    background: #EAEBED;
}
.tr-task-active {
    background-color: #EAEBED !important;
    font-weight: bold;
}
.secondary-top-menu {
    position: fixed;
    top: 0;
    z-index: 9;
    display: table;
    table-layout: fixed;
    width: 100%;
    background: #535C69;
    color: #fff;
}
.main-menu .main-menu-head::after,
.secondary-top-menu .main-menu-head::after,
.main-menu .secondary-menu-head::after,
.secondary-top-menu .secondary-menu-head::after {
    position: absolute;
    display: block;
    content: '';
    right: -219px;
    top: -78px;
    width: 200px;
    height: 200px;
    background: #535C69;
    box-shadow: none;
    -webkit-transform: rotate(45deg);
    transform: rotate(45deg);
    border-bottom: none!important;
}
.secondary-top-menu .secondary-menu-head {
    display: table-cell;
    vertical-align: top;
    position: relative;
    overflow: hidden;
    background: #535C69;
    border-bottom: none!important;
    height: 45px;
    width: 180px;
    padding-left: 10px;
}
.main-menu .main-menu-tail::after,
.secondary-top-menu .main-menu-tail::after,
.main-menu .secondary-menu-tail::after,
.secondary-top-menu .secondary-menu-tail::after {
    border-bottom: 0px;
}
.planfixcardmenu tr .link .menu-text .menu-text-project {
    font-size: 14px;
    margin-bottom: -2px;
    line-height: 26px;
}

.planner-block-list-h .planner-block-list-h-tail .sync-planner-block {
    right: 20px;
}
.sync-planner-block.active {
    background: url('http://scr.artuna.ru/YFb/1lock.png') no-repeat 0 16px;
    background-position: 0;
}
.sync-planner-block.active.synced {
    background: url('http://scr.artuna.ru/7T8/2lock.png') no-repeat 0 16px;
    background-position: 0;
}
}
