@-moz-document regexp("http://(fa1|fa2|www)\\.eldarya\\.(com|fr|pl|de|dk|es|hu|it|nl|ro|ru|se|com.br|uk)\\/.*") {
/*Lily en familier + profil*/  
 
  /*image(73x73px) of pet on header*/
  #pet-headerPortrait #pet-headerPortrait-base 
  {background: url("https://www.pixenli.com/images/1465/1465846184011717500.png") no-repeat !important;
   -webkit-filter: hue-rotate(0deg);
    filter: hue-rotate(0deg);
  }


  #pet-portrait {
    background : url("https://www.pixenli.com/images/1476/1476889496094724400.png") center no-repeat !important ;
    background-size : auto 450px !important ;
    height : 450px !important ;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}
#player-display-pet {
    background : url("https://www.pixenli.com/images/1476/1476889901043984600.png") center no-repeat !important ;
    background-size : auto 100% ;
    height : 340px !important ;
    width : 329px !important ;
  }

    #player-display-pet > img[src*="/static/img/pet/mood/profile/"] {opacity : 0 !important ;
  }

	#player-display-pet {
    background : url("https://www.pixenli.com/images/1476/1476889901043984600.png") center no-repeat !important ;

  }

    /*Infos*/

   #pet-info-name:before {
    content : "Lilymoe" !important ;
    font-size : 20px !important ;
  }
    
  #pet-info-name {
    font-size : 0 !important ;
  }  
  
  #pet-info-evolve > .on {font-size : 0 !important ;}
  
  .pet-info-evolve-icon {font-size : 13px !important ;}
  
  .pet-info-evolve-icon:after {
    content : " Lily s'est défnitivement attaché à toi. Il te suivra partout désormais" ;
  }
  
  #pet-info-racegender:before {
    content : "Gardien " !important ;
    font-size : 16px !important ;
    line-height : 20px !important ;
  } 
  
  #pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
  }  
  
  img[src*="/static/img/f.png"] {
    background : url("http://www.eldarya.fr/static/img/m.png") !important ;
    padding-left : 14px !important ;
    padding-top : 15px !important ;
    width : 0 !important ;
    height : 0 !important ; 
    margin-bottom : 5px !important ;
  }
 
 
 
 /*Nourriture - food*/

#pet-info-food:before {
    line-height: 15px;
    content : "Eau Purifiée" !important ;
    font-size : 20px !important ;
    margin: 200px auto auto 34px;
    
}
#pet-info-food > div {
    margin-top : 2px !important ;
    line-height: 15px;
    font-size: 12px;
    margin: 13px 0 0 100px;

}
  
 #pet-info-food > img {
  background : url("http://www.eldarya.fr/static/img/item/consumable/a4f6d5b6c1e9738a1c99816433c2c12b.png") !important ;
  background-size : 60px 60px !important ;
  margin-top : -5px !important ;
  padding-left : 60px !important ;
  padding-top : 60px !important ;
  width : 0 !important ;
  height : 0 !important ;
} 
  
  #pet-info-food {font-size : 0 !important ;}
    
  #pet-info-food > span {font-size : 16px !important ;}
}
