css:
@-moz-document url-prefix("http://www.tipponline.ch") {
/* hide */
div#FooterAbstand,
div#LanguageChoose,
div#TippSpielWahlButton,
div#TippSpielWahl,
div#Liganame,
div#TippspielName {
    display: none;
}
/* flex */
div#ContentBackGround {
    display: flex;
    justify-content: space-between;
    width: 100%;
    padding: 0;
    border: none;
    background: none;
}
div#content {
    flex: auto;
    margin: 0;
    border: none;
    background: none;
}
/* header */
div#Header {
    height: 10px;
    width: 100%;
    background: white !important;
}
/* navigation */
#navigation {
    background-image: none !important;
    border: none;
}
#navigation .buttonsnav a,
#navigation .buttonsnav a:visited,
#navigation .buttonsnav a:active {
    font-size: 1em;
    font-weight: 100;
    color: #E0F3DF;
    background-image: none;
    background: #00B500;
    border: 1px solid #FFF;
}
#navigation .buttonsnav a.buttonsnavActive,
#navigation .buttonsnav a.SubNavActive {
    color: #FFF;
    font-weight: 600;
}
#navigation .buttonsnav a.SubNav {
    padding-left: 13%;
    width: 93%;
}
/* stuff */
#content table {
    font-size: 1.1em;
}
#content table tr:hover {
    background-color: #D0D0D0;
}
#rightborder {
    background-image: none !important;
    border: none;
}
div[align="center"] {
    text-align: left;
}
}
