@-moz-document url-prefix("chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe/") {
#header {
    background-color: #101010;
	border-right: 1px dashed /*[[Color]]*/;
}
#header h1 {
    color: #fff;
}
legend#filters {
    color: #fff;
}
label[for="manage.onlyEnabled"] {
    color: #fff;
}
label[for="manage.onlyEdited"] {
    color: #fff;
}
fieldset {
    border-width: 1px;
    border-radius: 3px;
    border: 2px solid /*[[Color]]*/;
}
#search {
    background-color: #101010;
    border-width: 1px;
    border-radius: 3px;
    border: 2px solid /*[[Color]]*/;
    color: #fff;
}
button {
    background-color: /*[[Color]]*/;
    color: #fff;
    border: 1px solid /*[[Color]]*/;
    border-radius: 2px;
}
div#options {
    color: #fff;
}
a, a:visited {
    color: /*[[Color]]*/;
}
#installed {
    background-color: #101010;
    color: #fff;
    top: -12;
}
[style-id] {
    border-top: 2px solid /*[[Color]]*/;
}
html {
    background-color: #101010;
}
#enabled-label {
    color: #fff;
}
h2[id="mozilla-format-heading"] {
    color: #fff;
}
.applies-to label {
    color: #fff;
}
button.remove-applies-to {
    margin-left: 0.2rem;
}
button.add-applies-to {
    margin-left: 0.2rem;
}
section#options {
    color: #fff;
}
span#sections-heading {
    color: #fff;
}
label {
    color: #fff;
}
.CodeMirror {
    border: solid #27282e 1px;
}
/* Scrollbar */ 

::-webkit-scrollbar{width: 10px;}

::-webkit-scrollbar-thumb{background-color:rgb(35, 168, 245); border-radius: 0;}
::-webkit-scrollbar-thumb:hover{background-color:rgb(35, 168, 245);}

::-webkit-scrollbar-track{background-color:rgb(0, 0, 0);}


/* End Scrollbar */
#name {
    width: 100%;
    background: #101010;
    color: #fff;
    border: 2px solid /*[[Color]]*/;
    border-radius: 3px;
}
.applies-to li .applies-value {
    background: #101010;
    color: #fff;
    border: 2px solid /*[[Color]]*/;
    border-radius: 3px;
}
.applies-to li > *:not(button) {
    background: #101010;
    color: #fff;
    border: 2px solid /*[[Color]]*/;
    border-radius: 3px;
}
input[data-option="tabSize"] {
    background: #101010;
    color: #fff;
    border: 2px solid /*[[Color]]*/;
    border-radius: 3px;
}
select[data-option="keyMap"] {
    background: #101010;
    color: #fff;
    border: 2px solid /*[[Color]]*/;
    border-radius: 3px;
}
.CodeMirror { background: black !important; color: white !important; }
.CodeMirror-selected { background: #35493c !important; }
.CodeMirror-line::selection, .CodeMirror-line > span::selection, .CodeMirror-line > span > span::selection { background: rgba(53, 73, 60, 0.99) !important; }
.CodeMirror-line::-moz-selection, .CodeMirror-line > span::-moz-selection, .CodeMirror-line > span > span::-moz-selection { background: rgba(53, 73, 60, 0.99) !important; }

.CodeMirror-gutters { background: #002240 !important; border-right: 1px solid #002240 !important; }
.CodeMirror-guttermarker { color: white !important; }
.CodeMirror-guttermarker-subtle { color: #d0d0d0 !important; }
.CodeMirror-linenumber { color: #d0d0d0 !important; }
.CodeMirror-cursor { border-left: 1px solid white !important; }

.cm-keyword {  color: #CC7832 !important; }
.cm-atom { color: #FC0 !important; }
.cm-number { color:  #FFEE98 !important; }
.cm-def { color: #8DA6CE !important; }
span.cm-variable-2, .cm-s-vibrant span.cm-tag { color: #FFC66D !important}
span.cm-variable-3, .cm-s-vibrant span.cm-def { color: #FFC66D !important}
.cm-operator { color: #888 !important; }
.cm-comment { color: gray; font-weight: bold !important; }
.cm-string { color:  #A5C25C !important}
.cm-string-2 { color: red !important}
.cm-meta { color: #D8FA3C !important; }
.cm-builtin { color: #8DA6CE !important; }
.cm-tag { color: #8DA6CE !important; }
.cm-attribute { color: #8DA6CE !important; }
.cm-header { color: #FF6400 !important; }
.cm-hr { color: #AEAEAE !important; }
.cm-link { color: blue !important; }
.cm-error { border-bottom: 1px solid red !important; }

.CodeMirror-activeline-background {background: #27282E !important;}
.CodeMirror-matchingbracket {outline:1px solid grey !important; color:white !important;}
.cm-s-default .cm-qualifier {
    color: /*[[Color]]*/ !important;
}
h2 {
    color: #fff;
}
#lint caption {
    color: #fff;
}
tr.error {
    color: #fff;
}
tr.warning {
    color: #fff;
}
select[data-option="theme"] {
    background: #101010;
    color: #fff;
    border: 2px solid /*[[Color]]*/;
    border-radius: 3px;
}
}
