css:
@-moz-document domain("stopgame.ru") {
:root {
/*--font: Arial, Tahoma, Trebuchet MS, Segoe UI, Helvetica, sans-serif;*/
--fs: /*[[fs]]*/px;
/*[[cc]]*/

/*[[img]]*/

/*----------------------------------------------------------------------------------------------------*/
/*----------------------------------------------БРЕЙК-------------------------------------------------*/
/*----------------------------------------------------------------------------------------------------*/


/*{ font-family: var(--font) !important; font-size: var(--fs) !important}
/*Используемые шрифты*/

a.menu-drop-link:hover, .footer_direct_1, .live_on_main, .user-pad, .section-main-content, .main_text
{ background: var(--color1) !important}
/*Цвет фона*/

.game-about .par, h1, h2, h3, h4, h5, h6, div { color: var(--color5) !important; border-bottom: none !important; text-shadow: none !important; }
/*Цвет текстов*/

.section-menu, .section-footer, .main_text blockquote, .main_text .quotemain, .switcher3, .game-ratings, .right-block, .section-breadcrumbs, .title-actions, .review_image, .section-game-view .game-description
{ background: var(--color2) !important; }
/*Цвет подложек*/

.main_text blockquote, .main_text .quotemain, .review_image, .game-ratings, .section-breadcrumbs
{ border: 1px solid var(--color3) !important; color: var(--color5); line-height: 150% !important; text-shadow: none !important }
/*Параметры подложек*/

.section-carousel-parent, .nyroModalCont, .popup_rating, .search-form, .box2,  .menu-cell .menu-drop, .search-cell.active-search .search-additional, .search-cell.active-search .input-group
{ background-color: var(--color4) !important; background: var(--color4) !important; box-shadow: none;}
/*Третий уровень*/


.spoiler-title b:hover, .menu-div a.menu-link span, a { color: var(--color7) !important; transition: 0.2s !important;}

.right-block-block .right-block-link, .spoiler-title b, .user-logout a:hover, .user-short-data .title a:hover, .user-menu tr td a:hover, .fotorama__arr, .flex-direction-nav a:before, .game-favourite-actions .collection_change:hover a, a:hover, a:focus, .lent-title a, .blog-best .title a { color: var(--color7) !important; transition: 0s !important;}
/*Цвет ссылок*/

a, .lent-title a:hover, .blog-best .title a:hover { /*border-bottom: 1px solid var(--color10) !important; */transition: 0.1s; text-decoration: none !important; color: var(--color8) !important; }


a:hover, a:focus { /*border-bottom: 1px solid var(--color9) !important; */transition: 0s; text-decoration: none !important; }

.lent-title a, .blog-best .title a {transition: 0.6s !important;}
.lent-title a:hover, .blog-best .title a:hover {transition: 0.1s !important;}

.spoiler-title b:hover {border-bottom: 1px dashed var(--color7);}
.spoiler-title b {border-bottom: 1px dashed var(--color8);}
/*Оформление ссылок*/

.update, .switcher1 li, .colored-bg-orange, .game-favourite-actions, .game-passport-button, .colored-bg-green, .about_rating_link, .tab-stream li, .buttons a.button, .buttons button, .buttons input[type=button], .buttons input[type=submit], input[type="button"]
{ border-color: var(--color4) !important; color: var(--color5) !important; background: var(--color2) !important; text-shadow: none !important; transition: 0.2s; box-shadow: none !important; border-radius: 3px; vertical-align: middle !important;}

.update:hover, .title-menu li:hover, .switcher1 li:hover, .tab-stream li:hover, .buttons a.button:focus, .buttons a.button:hover, .buttons button:focus, .buttons button:hover, .buttons input[type=button]:focus, .buttons input[type=button]:hover, .buttons input[type=submit]:focus, .buttons input[type=submit]:hover, input[type="button"]:hover, input[type="button"]:focus
{ background: var(--color9) !important; color: var(--color7) !important; border-color: var(--color9) !important; transition: 0s;}

.tab-stream li:focus, input[type="button"]:hover, input[type="button"]:focus, .b-saved-stories-panel .ss-cats-list>a, .uical>.uical-head>.uical-top-btns>a:hover, .tags-bar>div.tb-tag>div:hover, table .menu li a:hover, .b-user-menu-list__count:hover, .b-button_type_save:hover, .pager ul li.no_border a:hover, .b-gtpost-builder__preview, .b-story__show-all:hover, .b-related-stories>a.rs-more-btn:hover { border: none !important; }
/*Кнопки и тэги*/



.menu-div .active a.menu-link, .menu-div a.menu-link:hover, .title-menu li.active, .sort_select span.active, .switcher1 li.active, sort_select span.active, .game-review-button, .write-review-button, .game-info-link a, .button-blue
{background: var(--gc) !important; border-color: var(--gc) !important; color: var(--color1) !important; text-shadow: none; transition: 0.2s;}

.game-review-button:hover, .write-review-button:hover, .game-info-link a:hover, .button-blue:hover
{background: var(--gc) !important; boborder-color: var(--gc) !important; color: #fff /*var(--color1)*/ !important; transition: 0s; -webkit-filter: saturate(1.2) brightness(1.1); }
/*Цвет зеленых кнопок*/


.iframe_h .iframe_ratio, .notice-wrap {display: none }
/*Отключаем элементы*/



textarea, input, input[type="password"] { border: 1px solid var(--color4) !important; background: var(--color1)!important; transition: 0.2s; color: var(--color5)!important; box-shadow: none !important }

textarea:hover, input:hover, input[type="password"]:hover { border: 1px solid var(--color8) !important; transition: 0s; }

textarea:focus, input:focus, input[type="password"]:focus { border: 1px solid var(--color8) !important; background: var(--color1)!important; box-shadow: rgba(0, 0, 0, .3) 0 1px 3px !important; color: var(--color5)!important; }
/*Поле ввода текста*/



.game-favourite-actions .game-favourite-count, .main_text .stop_rate, .users_list, .footer-bottom, .subscribe-form, .right-block-block, .lent-image img, .main-more-news, .lent-line .lent-block, hr { border-color: var(--color3) !important; }
/*Затемнение бордеров*/

.sect-video-hd .media-content, .menu-div .search-cell .search-submit, .lent-left, .lent-left-games, .live_on_main { border: none !important; }
/*Убираем бордеры*/

.user-pad, .title-alone, .other-data h4, .response-body, .responses-title
{ background: none !important; }
/*Убираем фон*/

.live_on_main {margin: 0; padding: 20px 0;}
.iframe_h, .iframe_wrapper {height: 540px;}

.footer_direct_1 {border-radius: 3px !important;}

.game-review-button a, .write-review-button a:before, .submit-topic-button:before, .write-review-button a {color: var(--color1) !important; transition: 0s !important;}
.img-tag, .menu-div a.menu-link:hover span, .vote-result, .general-title-dark-style .color-title, .title-menu li.active a, .game-data-list li:hover a, .created-by:hover, .switcher1 li.active a, .sort_select span.active a, .inTop, .game-review-button:hover a, .img-tag-rating, .icon-rating, .write-review-button:hover a:before, .write-review-button:hover a {color: #fff !important; transition: 0s !important;}

.main_text {font-size: var(--fs); line-height: 150%; padding: 30px}
.popup_rating .stars .star2 {background: url(http://i.imgur.com/m8wW0IC.png) -32px -181px;}
.popup_rating .stars .star3 {background: url(http://i.imgur.com/m8wW0IC.png) -64px -181px; color: #fff !important;}
.popup_rating .stars .star2:hover {background: url(http://i.imgur.com/m8wW0IC.png) -64px -181px;  color: #fff !important;}
.comment_level_icons div {background: url(http://i.imgur.com/m8wW0IC.png) 0 0px no-repeat;}
.sort_select span {border-color: var(--color8) !important; }
.inTop {background: #222}
.inTop {opacity: 0.5; z-index: -1;}
.second-announcer .flexslider li img:hover, .inTop:hover {opacity: 1; -webkit-filter: brightness(1.2)}
.switcher1 {background: none; border-bottom: 1px solid var(--color8);}
#top_hidden {background: var(--color9) !important;}
.flexslider:hover .flex-direction-nav .flex-next {right: 16px;}
.flexslider:hover .flex-direction-nav .flex-prev {left: 8px;}
.review_image {margin: 24px -31px; padding: 15px 0px 5px;}
.main_text .stop_rate img {margin: 0 16px;}
.main_text blockquote, .main_text .quotemain {padding: 10px 10px 10px 20px;}
.menu-div .separ {display: none}
.section-menu {padding-bottom: 3px;}
.second-announcer .flexslider li img, .lent-image img, a.nyromodal img, .section-game-view .game-image, .section-game-view .game-description, .user-pad, a.menu-drop-link:hover {box-shadow: none}
.lent-left, .lent-left-games {padding: 0px;}
.sect-video-hd, .list-type-view {margin: 0}
.lent-left, .lent-left-games {width: 760px;}
.user-menu tr td a, .section-header .social-links a, .user-logout a, .user-short-data .title a {color: #828282 !important;}
.sect-video-hd .media-content {padding: 0px;}
.iframe_wrapper {margin: 16px auto;}
.main_text.live_on_main {padding: 0; background: none !important;}

a.nyromodal img:hover {-webkit-filter: brightness(1.2)}


::-webkit-scrollbar { width: 10px !important; height: 10px !important; }
::-webkit-scrollbar-thumb { background: var(--color8) !important; }
::-webkit-scrollbar-track { background: #111 !important; }
::-webkit-scrollbar-track-piece { background: #111 !important; }
}

style_setting_options.value:
	--color1: #000; /*Цвет фона*/
	--color2: #0a0a0a; /*Цвет подложек*/
	--color3: #151515; /*Цвет обводки подложек*/
	--color4: #222; /*Обводка тэгов и кнопок*/
	--color5: #828282; /*Цвет текста*/
	--color7: #999; /*Цвет ссылок #37769E*/
	--color8: #CA0000; /*Комплементарный цвет #478544*/
	--color9: rgba(202, 0, 0, 0.35); /*Комплементарное подчеркивание #1E341D;*/
	--color10: rgba(255, 255, 255, 0.15); /*Подчеркивание ссылок*/
	--gc: var(--color8)}

.menu-div .search-cell .search-submit {background: url(http://images.stopgame.ru/site/icons/icon_search.png) no-repeat transparent !important; -webkit-filter: invert(1)}
img#update-comments  {-webkit-filter: invert(1)}
img { transition: 0.4s; -webkit-filter: brightness(0.6) saturate(40%); }
.fotorama__img, img:hover {transition: 0.1s; -webkit-filter: brightness(1.0) saturate(100%);}
/*Затемняем картинки*/
