css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("disqus.com") {

body { 
color: #666;
}

.site-nav--wrapper { 
background: url("http://i.imgur.com/kgyLeGF.png") repeat-x;
background-size: auto 100%; 
border-bottom: 1px solid #101010;
}

.module-header, 
.module-footer { 
border-color: #333;
}

.cover-profile-avatar img, 
.fieldset__avatar--img img { 
border: 1px solid #646464;
box-shadow: 3px 3px 3px #000;
}

.cover-profile-name { 
color: #2E9FFF;
}

.cover-nav-wrapper { 
border-color: #373737;
}

.cover { 
font-family: sans-serif; 
background: rgba(25,25,25,.9) !important;
background-clip: padding-box !important;
border-bottom: 1px solid rgba(0, 0, 0, 0);
box-shadow: 0 0 10px 4px rgba(0, 0, 0, 0.8);
}

#container-main { 
background: url('http://i.imgur.com/hn3H9r8.jpg') 100% 100% !important; 
background-repeat: no-repeat !important; 
background-attachment: fixed !important; 
background-position: 25% !important; 
-webkit-background-size: cover !important;
-o-background-size: cover !important; 
background-size: cover !important;  
overflow-y: auto !important; 
overflow-x: hidden !important; 
}

.link-gray-darker, 
.module-header__title, 
.card-content__body .discussion-title, 
.card-profile .card-reason .link-community { 
color: #DDD;
}

.card-reason, 
.card-profile .card-reason .link-discussion, 
.link-gray, 
.link-inner-gray a, 
.card-comment .comment-message, 
.no-touch .link-gray-light:hover, 
.card-comment .card-comment-truncated, 
.no-touchevents .link-gray-light:hover { 
color: #777;
}

.nav--aside .nav__item.active, 
.nav-stacked .nav__item.active, 
.nav--no-aside .nav__item.active { 
background-color: #2B2B2B;
}

.nav-tabs, 
.nav__item { 
background-color: #161616;
border-color: #333;
}

.nav--aside, 
.nav-stacked, 
.nav__spacing, 
.nav--no-aside { 
border: transparent;
}

.form, 
.card-wrap, 
.content__wrap, 
body:not(.home-drawer) .card-notif, 
body:not(.home-drawer) .card-profile, 
body:not(.home-drawer) .card-profile-forum { 
box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
background-color: #161616;
border: 1px solid #333;
border-radius: 7px;
}

.card, 
.form__header, 
.form__footer, 
.border-bottom, 
.recommended-wrap, 
.card__reason:first-child, 
.card-notif-upvote .card-comment .comment-body, 
.card-notif-reply .card-comment .parent .comment-body { 
border-color: #333;
}

.aside__wrap { 
box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
background-color: #161616;
border: 1px solid #333;
border-radius: 3px;
}

.layout__aside .text-smaller { 
box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
background-color: #161616;
border: 1px solid #333;
border-radius: 3px;
padding: 10px; 
}

div[data-role="content-area"] .card--empty { 
box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
background-color: #161616;
border: 1px solid #333;
border-radius: 3px;
padding: 10px; 
margin-bottom: 10px;
}

.input--textbox, 
.fieldset__avatar--options select { 
background: #2C2C2C;
border-color: #666;
color: #CCC;
}

.layout .more-wrapper { 
background: 
linear-gradient(to bottom,rgba(73,69,72,1) 0, rgba(35,31,32,1) 100%);
box-shadow: 0 -2px 5px 1px rgba(0,0,0,.7), 
0 75px 20px 1px rgba(0,1,0,.5);
border: 1px solid #333;
}

.aside-list__item { 
border: transparent;
}

.dropdown-menu { 
box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
background-color: #161616;
border: 1px solid #333;
border-radius: 3px;
}

.dropdown-settings .divider { 
border-color: #333;
}

.dropdown-menu:before { 
border-color: transparent transparent #161616;
}

.dropdown-menu::after { 
border-color: transparent transparent #333;
}

.card--notification .card-comment .reply, 
.card--notification .card-comment .reply-disabled, 
.card--notification .card-comment .view-comment, 
.no-touch .card--notification .card-comment .view-comment, 
.no-touchevents .card--notification .card-comment .view-comment { 
background: #232323;
border-color: #333;
box-shadow: 0 0 0 1px #333 inset;
}

.label--count, 
.no-touch .button-outline, .no-touchevents .button-outline, 
.no-touch .button--recommend, .no-touchevents .button--recommend {
border-color: #333 !important;
background-color: #232323 !important;
}

.carousel-control { 
background: rgba(25,25,25,.9);
}

.carousel-control:hover { 
background: rgba(25,25,25,.7);
}

  .module__wrapper::before { 
background-color: #000;
}

.card__additional { 
background-color: #222;
border-color: #333;
border-radius: 0 0 7px 7px;
}

.nav-tabs { 
border-radius: 7px 7px 0 0;
}

.home-drawer #container-main > div, 
.home-drawer.active .drawer-footer { 
background-color: #222;
}

.home-drawer .card-profile, 
.home-drawer .cover-profile, 
.home-drawer .notifs-fixed, 
.home-drawer #container-main, 
.home-drawer.active .drawer-footer { 
border-color: #333;
}

  .curtain-truncate { 
border-color: #2E9FFF;
}

}
