@-moz-document url("http://www.imdb.com/"), url-prefix("http://www.imdb.com/"), domain("imdb.com"), regexp("http://www.imdb.com/") {
#navbar {
  background: none repeat scroll 0 0 rgba(0, 0, 0, 255);
  border-top: medium none;
  color: #000;
  font: 10pt Verdana,Arial,sans-serif;
  height: 78px;
  margin: 15px auto auto 0;
  padding: 0;
  position: relative;
  width: 1020px;
  z-index: 200;
  border-radius: 0px;
  margin: -15px;
}
 #pagecontent {
    background: -webkit-linear-gradient(top, rgba(0,0,0,0.3) 0%, rgba(0,0,0,0) 7px);
    background: linear-gradient(top, rgba(0,0,0,0.3) 0%,rgba(0,0,0,0) 7px);
    background-repeat: no-repeat;
    background-size: 100% 7px;
    background-color: #000;
    padding-top: 1px;
    padding-bottom: 1px;
    margin-top: -1px;
    margin-bottom: -1px;
} 
  #content-2-wide #main .article, #content-2-wide .main .article {
    background-color: #FFF;
}
div.ab_hero {
    padding: 20px 21px 9px 20px;
}
div.ab_hero {
 background-color: #000;
    color: #747474;
}
 ul.main_nav li p.navCategory {
 color:  #FFF;
} 
  
.yn {
display: none;
}
#content-2-wide::before {
    content: '';
    background-color: rgba(168,168,168,0.1);
    border-left: solid 3px #000;
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    width: 0;
}
 #content-2-wide #sidebar, #content-2-wide .maindetails_sidebar {
    width: 300px;
    font-size: 100%;
    margin-right: 20px;
    float: right;display: none;
} 

 #pagecontent {
margin-left: -2px;
   width: 1010px;
