}
 div#content-2-wide {
    margin-top: -10px;
} 
  #content-2-wide #main, #content-2-wide .maindetails_center {
    width: 995px;
    float: left;
    margin-left: 0px;
    border-radius: 0px;

}
 .article, .aux-content-widget-2, .aux-content-widget-3, .article.on-tv, .aux-content-widget-4 {
    border-radius:  0px;
} 
 .redesign .article.highlighted {
    background: #EFE3A4;
    border: 0px double white;
    margin: -6px -3px 2px -3px;
    padding: 8px 12px;

} 

td#img_primary {
border: Dpx;
}
 #content-2-wide #sidebar {
    background-color: transparent;
    float: right;
    margin: 0;
    max-width: 0px;
    width: 0px;

} 
#nb_search .primary.btn {
display: none;
}
 .navbarSprite {
    background-color: transparent;
    background-image: none;
    background-attachment: scroll;
    background-repeat: no-repeat;
} 
ul.main_nav li.css_nav_item:hover, ul.main_nav li.js_nav_item:hover {
    background: #444444;
    background: -webkit-linear-gradient(top, #373737 15%, #373737 50%, #373737 85%);
    background: linear-gradient(to bottom, #373737 15%,#373737 50%,#373737 85%);
}
#footer {
display: none;
}  
.rightcornerlink {
display: none;
}
 h1, h2, h3 {
display: none;
} 
div.article.contribute .form-box {
display: none;
}
 div.article.contribute .button-box {
display: none;
} 
  
#content-2-wide #main, #content-2-wide .main {
    background-color: transparent;
    float: left;
    margin: 0;
    max-width: 1005px;
    padding: 0;
    width: 1005px;
}  
  #navbar select {
    display: none;
}

  .btn.primary {
    border-color: #000;
    background-color: #000;
}
#navbar div.quicksearch_dropdown_wrapper {
  background-color: #f2f2f2;
  box-shadow: none;
  height: 29px;
  overflow: hidden;
  position: absolute;
  right: 332px;
  top: 0;
  width: 100px;
}
#styleguide-v2 {
  background: none repeat scroll 0 0 #000;
  margin: 0;
}
.heroic-overview .vital {
    background-color: #000;
    color: #fff;
}
#home_img {
  background-image: url("http://s32.postimg.org/p48zx6rc5/sadfsd.png");
  background-position: 0 -70px;
  border: medium none;
  display: block;
  height: 50px;
  left: 12px;
  position: absolute;
  top: 6px;
  width: 101px;
  z-index: 10000;
  margin-top:  10px;
}
  #consumer_main_nav {
    bottom: 0;
    left: 240px;
    position: absolute;
    top: 20px;
}
  #consumer_user_nav {
display: none;
}
#content-2-wide #sidebar {
display: none;background-color: #000;
}
ul.main_nav li.spacer {
  background: none repeat scroll 0 0 rgba(0, 0, 0, 0);
  height: 46px;
  padding: 0;
  width: 1px;
}

#nb20 {
  background-color: #000;
  background-image: none;
  background-position: 72px bottom;
  background-repeat: repeat-x;
  margin-top: 0;
  padding-top: 1px;
}

.redesign .article.highlighted {
  background: none repeat scroll 0 0 #FFD800;
}
#nb_extra {
display: none;
}
#nb_search .primary.btn {
    background: none repeat scroll 0 0 #FFD800;
    border-radius: 0 0px 0px 0;
    float: right;
    height: 29px;
    margin: 0px 17px;
    vertical-align: middle;
}
#navbar input {
display: none;
}
  #navbar div.quicksearch_dropdown_wrapper {
display: none;
}
.titlePageSprite {
  background-image: url("http://s29.postimg.org/5cwanamuv/title_Page_Sprite_407338954_V.png");
  display: inline-block;
  vertical-align: middle;
}

.btn2.primary {
  background: none repeat scroll 0 0 #F3CE00;
  border-radius: 6px;
}

.btn2 {
  background: none repeat scroll 0 0 #F3CE00;
  border-color: #F3CE00;
  border-radius: 6px;
  border-style: solid;
  border-width: 1px;
  color: #000000 !important;
  cursor: pointer;
  display: inline-block;
  font-family: Verdana,Arial,Sans-serif;
  font-weight: normal;
  margin: 0;
  padding: 0;
  text-align: center;
  text-decoration: none;
  vertical-align: middle;
}

a:link {
  color: #000;
  text-decoration: none;
}

.aux-content-widget-2, .aux-content-widget-3, .article.on-tv, .aux-content-widget-4 {
  background: none repeat scroll 0 0 #E8E8E8;
  border: medium none;
  border-radius: 12px;
  box-shadow: none;
  margin-bottom: 7px;
  padding: 10px 12px;
  position: relative;
  width: auto;
  word-wrap: break-word;
}

.navbarSprite {
  position: fixed;
  width: 1008px;
  z-index: 1000;
}

#navbar input {
  background: none repeat scroll 0 0 #f2f2f2;
  border: medium none;
  border-radius: 3px 0 0 3px;
  box-shadow: none;
  font: 14px Verdana,Arial,sans-serif;
  height: 19px;
  padding: 5px 8px;
  vertical-align: middle;
  width: 419px;
}

#nb_search .magnifyingglass {
  margin-top: -9px;
}

#pagecontent {
  padding-top: 90px;
}
}

