css:
@-moz-document domain("legendas.tv") {
html, body {
    background-image: url("data:image/gif;base64,R0lGODlhBAAEAKECABERESQkJP///////yH5BAEKAAIALAAAAAAEAAQAAAIFhB6nhlIAOw==") !important;
    background-repeat: repeat !important;
    background-color: #161616 !important;
}

.slider_wrapper .js_tooltip > div .tooltip {
    display: none;
}

p {
    color: #888!important;
}

span {
    color: #888!important;
}

div#banner_lateral {
    display: none;
}

.js_tooltip > div:hover {
    background-color: transparent!important;

}

a {
    color: #8899a6;
}

.upper {
    display: none;
}

.banner_728x90 {
	display:none
}

textarea:focus, input:focus {
    color: #ccc!important;
}

header .searchbar input[type="text"] {
    border: 1px dashed #bcbec0;
    opacity: 0.3!important;
}

header .searchbar input[type="submit"] {
    background: #8899a6 url(http://i.legendas.tv/sprite_1.png) -83px 0px;
    opacity: 0.3!important;
}

.bt_seta_download {
    background: #000;
}

img {
    -webkit-filter: grayscale(100%);
}

img:hover {
    -webkit-filter: grayscale(0%);
}

.middle .bigtitle, .middle .title {
    background: none;
    display: none;
}

.js_tabs.style_1 > nav {
    background: none;
}

middle section {
    margin-top: 40px;
}

.middle article h2 {
	background: none;
}

.middle .wrapper .film h3 a {
    color: #8899a6!important;
}

.middle .slider_wrapper .slider .item a span {
    color: #8899a6!important;
}

.middle .pagination_wrapper .pagination {
    background-color: transparent;
	border: none;
}

.middle .pagination_wrapper .pagination button.active {
    background-color: #8899a6;
    color: #fff;
}

.middle .pagination_wrapper {
    border-top: none;
}

.bigtitle a {
    color:#8899a6!important
}

.middle .slider_wrapper .slider {
    border-bottom: none;
}

.bt_seta_download .seta {
    background: none!important
}

.bt_seta_download {
    padding: 2px 12px 2px 12px;
}

.bt_seta_download:hover {
    background-color: #8899a6;
    padding: 2px 12px 2px 12px;
}

.middle article h2 {
    color:#8899a6!important;
}

.middle article h2 a {
    color:#8899a6!important;
}

.middle .slider_left, .middle .slider_right {
    opacity: 0.3!important;
    -webkit-filter: grayscale(60%);
}

.js_tabs.style_1 > nav a {
    color: #ccc!important;
}
.js_tabs.style_1 > nav a:hover, .js_tabs.style_1 > nav a.active {
    background: #fff;
    color: #161616!important;
}

header .menu button {
   opacity: 0.3!important;
}

.logo {
    opacity: 0.3!important;
}

.middle .bar {
    background-color: transparent!important;
}

.middle article > div {
    border-bottom: none!important; 
}

.busca article > div a, .busca article > div > div > p a {
    color: #888!important;
}

.busca .pack {
    background-color: #ccc!important;
}

.download section {
    border-top: none!important;
}

h1 {
	color: #eee
}

article.js_tooltip img {
    -webkit-filter: grayscale(0%)!important;
}

.download button:hover {
    background-color: #2c61aa;
}

.middle .bar:after {
    background-color: #8899a6;
}

.middle .slider_wrapper .slider .item a span.selo_temp {
    z-index: 1;
    color:#f5f5f5!important
}

#sugestao-legenda-box {
    z-index: 2;
}

.download button {
    background: #101010!important;
}

.icon_arrow:after {
    background: none; 
}

.download section.first img {
	-webkit-filter: grayscale(0%);
}

.download button:hover {
    background-color: #8899a6!important;
}
}
