css:
@-moz-document domain("www.recode.net") {
body {
    font-family: Helvetica, sans-serif;
    background-color: #0D0D0D !important;
    color: #3b3b3b;
    line-height: 1.6;
    text-rendering: optimizeLegibility;
    vertical-align: baseline;
}

.c-global-header__links {
    background-color: #131313 !important;
}  
  
.c-global-header:before {
    background-color: #131313 !important;
    bottom: 0;
    content: '';
    left: 0;
    position: absolute;
    right: 0;
    top: 0;
    z-index: 2;
}

.c-global-header {
    border-bottom: 1px solid;
    border-color: #131313 !important;
    color: #3b3b3b;
    position: relative;
    width: 100%;
    z-index: 5000000;
}

.l-main-content {
    background-color: #131313 !important;
    position: relative;
    z-index: 0;
}

.c-page-title {
    color: #7d7d7d !important;
    padding-top: .6rem;
}

.c-global-header a {
    color: #7d7d7d !important;
}
  
.c-entry-content p {
    font-size: 18px;
    color: #7d7d7d !important;
}

.c-entry-content a {
    border-bottom: 0px solid !important;
    border-bottom-color: #ed2b23;
    color: #ed3b23 !important;
}

.c-tab-bar {
    background: hsl(0, 0%, 7%) !important;
}

.c-tab-bar__display {
    background: transparent !important;
    border-top: 0px solid #e7e7e8 !important;
    display: table;
    opacity: 0;
    padding: 0 10px;
    -webkit-transition: .2s opacity;
    transition: .2s opacity;
    width: 100%;
}

.c-entry-content a {
    border-bottom: 1px solid;
    border-bottom-color: #ed2b23;
    color: #ed2b23 !important;
}
}
