css:
@-moz-document domain("cowlevel.net") {
body,
.setting-list,
.comment-list-sub-box {
    background-color: lightgreen !important;
}
.red{
	color: Chartreuse !important;
}
.wangEditor-drop-panel, .txt-toolbar{
    background: seagreen    !important;
}
.wangEditor-container{
    background: lightgreen    !important;
    color: darkgreen !important;
}
.main {
    background-color: #B93D3D;
}
.wangEditor-menu-container{
    background: MediumSeaGreen;
    border-bottom-color: SeaGreen;
}
.wangEditor-menu-container .menu-item .active{
    background-color: SeaGreen;
}
.tab-btn{
    color: yellow ;  
}
.tab-btn:hover {
    color: green !important;
}
.gm-layer, .edit-post-layer, .index-layer, .index-layer-2{
    background: IndianRed !important;
    color: lightgreen;
}
.cl-card{
    background-color: IndianRed !important;
}
.swiper-slide{
    background-color: IndianRed !important;
}
.cl-btn{
    background: LimeGreen !important;
}
.cl-btn i{
    color: lightGreen !important;   
}
.grey2{
    color: lightgreen !important
}
.btn-card-border i{
    color: lightGreen !important;
}
.btn-v3{
	border-top-color:lightgreen;
    border-right-color:lightgreen;
    border-bottom-color:lightgreen;
    border-left-color:lightgreen;
    color: lightgreen;
}
.box-shadow-0-8-16 {
    box-shadow: red 0 8px 8px;
}
.header-nav {
    background-color: #63E81F !important;
    color: darkgreen;
    transition: all 0.25s ease;
}
.header-nav:hover {
    opacity: 1;
}
.profile .nav-list li a {
    color: darkgreen !important;
}
.main,
.v2-main,
.v3-main {
    color: lightgreen !important;
}
.header-nav .logo a {
    color: white !important;
}
.beta {
    color: #FFF !important;
}
.text-shadow3 {
    text-shadow: green 1px 1px 1px;
}
.quick-href a {
    color: lightgreen !important;
}
.grey {
    color: #666 !important;
}
.grey5 {
    color: #76EE00 !important;
}
.grey6 {
    color: pink !important;
}
.gl-user-card-box .grey6,
.gl-user-card-box .white {
    color: #fff !important;
    text-shadow: 1px 1px 1px #000;
}
.grey8 {
    color: lightgreen !important;
}
#search-result-box {
    background-color: #C1FFC1 !important;
}
#search-result-box a {
    color: #667D0F !important;
}
.search-game-result li:hover {
    background-color: #d1d1d1 !important;
}
.title-grey {
    color: springgreen !important;
}
.white,
.white2,
.item-tit-a {
    color: PaleGreen !important;
}
.part-right .item-tit-a {
    color: #fff !important;
}
.card-list .item-tit {
    color: #fff !important;
}
.gl-hover-text-3:hover {
    color: #448 !important;
}
.tab-left .vote-btn-v2 {
    color: lightgreen !important;
}
.vote-btn-v2{
    border-top-color: lightgreen;
    border-top-style: solid;
    border-top-width: 3px;
    border-right-color: lightgreen;
    border-right-style: solid;
    border-right-width: 3px;
    border-bottom-color: lightgreen;
    border-bottom-style: solid;
    border-bottom-width: 3px;
    border-left-color: lightgreen;
    border-left-style: solid;
    border-left-width: 3px;
}
.vote-btn-v2:hover {
    background-color: rgba(255, 66, 95, .4) !important;
}
.gl-load-more-btn {
    background-color: rgba(17, 17, 34, .2) !important;
}
.line,
.line2,
.line3 {
    background: #112 !important;
    opacity: 0.3;
}
.h-tag li {
    border: 1px solid rgba(17, 17, 34, .3) !important;
}
.h-tag li.on,
.h-tag li:hover {
    box-shadow: 1px 2px 3px #666 !important;
}
.nav-list li::after {
    background: #f6f6f6 !important;
}
.notice-tip-window {
    background: #B93D3D !important;
}
.notice-tab-list li.on i {
    color: rgb(255, 66, 95) !important;
}
.notice-tab-list li.on .agree-tag {
    border-bottom: 14px solid rgba(255, 66, 95, .9) !important
}
.notice-tab {
    color: #112 !important;
}
.notice-tab li.weidu {
    background-color: #ddd !important;
}
.star-level-box .fa,
.clock-level-box .fa,
.star-color,
.star-list {
    color: #fa0 !important;
}
.header-nav input {
    background-color: rgba(39, 40, 57, 1) !important;
    box-shadow: inset 0px 2px 4px 0px rgba(0, 0, 0, 0.50) !important;
}
input::-moz-placeholder {
    color: #F1F1F1 !important;
}
.global-search-box {
    color: #C1FFC1 !important;
}
.global-search-box input {
    background-color: green !important;
    box-shadow: inset 0px 2px 4px 0px gray !important;
}
.quick-href.on {
    background: rgba(0, 0, 0, .1) !important;
}
.repassword-box h2,
.notify-box h2 {
    border-bottom: 1px solid #224 !important;
}
.bg-gradient,
.bg-gradient-2 {
    background: -moz-linear-gradient(top, rgba(241, 241, 241, .1), tomato) !important;
    background: -webkit-linear-gradient(top, rgba(241, 241, 241, .1), tomato) !important;
}
.vote-btn-v3 .agree-tag {
    border-bottom: 14px solid #224 !important;
}
.grey3 {
    color: #112 !important;
}
.gl-hover-text {
    color: GreenYellow !important;
}
.achievement-list span {
    color: #112 !important;
}
.tip-window::after {
    border-bottom: 16px solid rgba(241, 241, 241, 1) !important;
}
.tip-window {
    border: 1px solid rgba(255, 66, 95, .9);
    box-shadow: 1px 4px 5px rgba(0, 0, 0, .5);
}
.wangEditor-container .wangEditor-btn-container-btn-default {
    color: #224;
}
.wangEditor-container .wangEditor-btn-container,
.wangEditor-container .wangEditor-textarea,
.wangEditor-container .wangEditor-drop-menu,
.wangEditor-container .wangEditor-drop-menu a,
.wangEditor-modal-container .wangEditor-modal {
    background-color: MediumSeaGreen !important;
    color: #224;
}

.wangEditor-container .wangEditor-btn-container-group {
    border-right: 1px solid rgba(0, 0, 0, .1);
}
.wangEditor-modal-container .wangEditor-modal-header-line {
    border-top: 1px solid rgba(0, 0, 0, .1);
}
.wangEditor-modal-container .wangEditor-modal input[type="text"] {
    color: #224;
    padding: 7px;
}
}

@-moz-document regexp("^[^.]*cowlevel.net/about") {
.red {
    color: #C1FF6F;
}
.main{
    color: #e54e4e !important;
  }

}

@-moz-document url("https://cowlevel.net/") {
.grey5{
    color: rgba(255,255,255,.7) !important;
  }
}
