css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("skycitizen.net") {
/*[[1]]*/
/*[[2]]*/
.popup-close {
    background-color:#FFF !important;
    color:#FFF !important;
    position: absolute !important;
    top:-20px !important;
    right:100%  !important;
    text-shadow: 0 0 2px #FFF  !important;
    height:100% !important;
    padding:0px 10px!important;
    width:5px  !important;
}
#top-nav{z-index:1000 !important;}
.works-grid .large-grid:nth-child(n+10) ~ li {
    display: list-item !important;
}
.works-grid .medium-list:nth-child(n+6) ~ li {
    display: list-item !important;
}
}

@-moz-document url-prefix("http://www.skycitizen.net/?view=POST") {
#post-comments-container{
	opacity: 1 !important;

}
#post-comments{width:440px !important;position:absolute !important;right:-50px !important;}
.post-comments-pagination{position:absolute !important;width:500px !important;}
.hangout-section{position:relative !important;top:165px !important;width:400px !important;}
.hangout-section > .message-container{margin-top:60px !important; }
    
#left-sections{width:100% !important;}
#post-content{width:calc(100% - 200px) !important;}
    
}

style_setting_options.value:
.small-popup{padding-top:0px !important;margin:0 !important;width:101% !important;border-right: 1px solid rgba(0,0,0, 0.2)  !important;height:100% !important;left:0px !important;margin-top:-30px !important;}
.popup-backdrop{display:none !important;}
._post_edit_popup .small-popup.relative{position:absolute !important;height:100% !important;}
.message-container{padding-right:10px !important;}
.popup-section{padding:0px !important;width:calc(100% - 340px) !important;padding-top:0px !important;margin-top:0px !important;top:30px !important;}
.content.form.pr-5 #text ~.relative a:nth-child(3){right:110px !important;}
#right-sections #text ~.relative a:nth-child(3){right:20px !important;}
.small-popup #post-nav-bottom{position: fixed !important;width:calc(100% - 675px) !important; }
.post-comments-pagination{position: fixed !important;width:373px !important; z-index:10000 !important;padding-top:3px !important;background-color:white !important;}
.message-container.relative.pl-5.pr-5.mb-10{padding-bottom:30px !important;}
.popup-section .relative.pd-5.pt-10.message{width:320px!important; margin-left:-5px !important;}
.col-md-8.col-md-offset-2{margin-top:20px!important; }

