    [type=reset]{display: none}
    [name=addPhoto]{position: fixed; left: 1080px; bottom: 110px;}
    [name=delPhoto]{position: fixed; left: 1080px; bottom: 70px;}
    
    /*设置传稿数量计数*/
    #photoTable [type=file]{
        counter-increment: file;}
    div:before{
        position: fixed;
        left: 1100px;
        bottom: 155px;
        content: counter(file);
        display:inline-block;
        width: 35px;
        height:35px;
        text-align: center;
        line-height: 35px;
        border-radius:100%;
