css:
/* @import url(https://maxcdn.bootstrapcdn.com/font-awesome/4.1.0/css/font-awesome.min.css); */
@-moz-document domain("www.chatwork.com") {
.wrapper,
.globalHeader,
.numAdminCount,
.chatListUnreadNum,
.contentHl,
.mainContentHl,
.sideContentHl,
.chatSendArea,
.button {
	background-image: none;
	-webkit-box-shadow: none;
	box-shadow: none;
	border-radius: 0;
}
.promotionArea {
	height: 0;
	display: none;
}
.chatRoomListArea {
	padding-bottom: 141px;
}
.globalHeader .ico35AdminTask,
.globalHeader .ico35AdminFile,
.globalHeader .ico35AdminContact,
.globalHeader .ico27Menu {
	text-indent: inherit;
	color: #fff;
	background: none;
	font-size: 25px;
	line-height: 1.4;
	height: inherit;
}
.globalHeader .ico35AdminTask:before {
	content: "\f0ae";
	font-family: FontAwesome;
}
.globalHeader .ico35AdminFile:before {
	content: "\f07b";
	font-family: FontAwesome;
}
.globalHeader .ico35AdminContact:before {
	content: "\f0c0";
	font-family: FontAwesome;
}
.globalHeader .ico27Menu:before {
	content: "\f05a";
	font-family: FontAwesome;
}
.actionArea {
	top: 0;
	bottom: inherit;
}
.actionArea .actionNav {
	border: none;
	-webkit-box-shadow: none;
	box-shadow: none;
}
.actionArea .showAreatext {
	display: none;
}
.chatRoomListArea .menuListTitle>li {
	border: none;
	overflow: hidden;
	padding: 2px 0px;
}
.chatRoomListArea .menuListTitleSelected,
.chatRoomListArea .menuListTitleSelected:hover {
	color: inherit;
	background-image: none;
	border: 0;
	text-shadow: inherit;
}
.chatListMeta {
	left: 44px;
}
.incomplete {
	position: absolute;
	top: 4px;
	right: 15px;
}
.chatListPin {
	top: 8px;
}
.menuListTitleSelected .icoFontActionTask,
.menuListTitleSelected .incomplete li {
	color: inherit;
}
.chatInfo {
	color: #082784;
	font-weight: bold;
}
.chatTimeLineMessage .chatTimeLineMessageInner,
.chatTimeLineMessage .chatTimeLineMessageHover {
	padding: 7px 15px;
	border: none;
}
.chatTimeLineBorder {
	border: none;
}
* {
	border-radius: 0 !important;
}
.chatRoomMemberListItem img,
.headerSearch .icSearch,
.chatTimeLineMessageArea pre ._avatar {
	border-radius: 50% !important;
}
.roomIcon .avatarMedium {
	border-radius: 3px;
}
.unreadTimeLine {
	border-radius: 4px !important;
	background-color: #163AE8;
}
.avatarSpeaker .avatarMedium {
	border-radius: 18px 18px 0 18px !important;
}
#_roomListItems {
	padding-left: 6px;
}
}
