css:
@-moz-document domain("startpage.com"), domain("ixquick.com") {
#home_wrap,
#results,
#content-box,
#wrapper,
#page-wrapper,
.sidebar-collapse,
.text-fields,
input,
.input_field,
.wrapper-dropdown,
.dropdown li,
.slide-menu li,
#footer,
.home-wrapper,
#pics-lemmon-slider-id,
.dropdown li:hover,
#menu-id ul li a:hover,
.set-heading,
.toggle-content,
.toggle-label,
.navbar .navbar-header {
    background-color: #202020 !important;
}
.navbar .navbar-header {
    background-image: none !important;
}
#pagenavigation #jumpsbar span.active,
#pagenavigation .numbers span.active {
    color: #000000 !important;
    background-color: #648AC6 !important;
    background-image: none !important;
}
.result {
    margin-bottom: 3px !important;
}
.sidebar-collapse {
    margin-bottom: 3px !important;
}
#field_size,
.settings .form-group .input-group input {
    border: 1px #648AC6 solid !important;
}
#footer,
.direct_results_box {
    border-top: 1px #648AC6 solid !important;
}
.side_list_menu {
    border-bottom: none !important;
}
.top_menu a:hover,
#menu-id ul li a:hover {
    text-decoration: underline !important;
}
#sponsored_container1, #sponsored_csa2 {
    display: none !important;
}
.set-heading {
    margin-bottom: 3px !important;
    border-radius: 15px !important;
}
.top_menu a,
#head_left a,
.result_url_heading,
.result,
#results .result h3 a,
.side_list_menu li a,
#head_left a,
.slide-wrapper ul li a,
#results .result h3 a,
.slide-wrapper,
.classified .relatedlist_wrap p a,
#bookmark_tip,
p.sitelinks a:visited,
p.sitelinks a,
.sellerrating a:visited,
.side_list_menu li a,
#head_left.non_js_tab a.active,
#pagenavigation a,
.content-box .heading .tips a, .content-box .heading .tips a,
.dropdown li a,
.content-box .input-group-addon:first-child,
input#search-btn,
.select-style,
.toggle-label {
    color: #5778AD !important;
}
input {
    color: #FFFFFF !important;
}
.result .url,
#side_bar2 ul#settings li a {
    color: #3C733D !important;
}
.result .desc,
input#query_top,
input#query,
.classified .relatedtext,
input.form-control {
    color: #A5A5A5 !important;
}
#results .result h3 a:visited,
.result a:visited .result_url_heading {
    color: #7B6296 !important;
}

#head_left {
    border-bottom: none !important;
}
.navbar-default,
.slide-wrapper .slide-menu>li:last-child,
.title_wrapper_small,
.content-box .heading,
.content-box .heading .tips a {
    border-bottom: solid 1px #5778AD  !important;
}
#field_size,
.set-heading,
input#search-btn {
    border: 1px #5778AD solid !important;
}
.navbar-default.navbar-static-side {
    border-bottom: none !important;
}
.slide-wrapper .slide-menu,
.slide-wrapper .slide-menu>li,
.classified,
.links_n.active,
.select-style,
.no-js .toggle-content {
    border-color: #5778AD !important;
}
.no-js .slide-wrapper {
    border-bottom: none !important;
}
.slide-wrapper {
    border-top: solid 1px #5778AD !important;
}
.slide-wrapper li a,
div.text_desc, 
span.any-span,
span.ad-search
{
    color: #9E9E9E !important;
}
.proxy,
p#caption a {
    color: #666 !important;
}
.settings_cont.inner-pages.three_line_menu,
.air-theme .select-style {
    background: none !important;
}
.numbers_st .i_next,
.numbers_st .i_prev {
      display: none;
}
.navbar-brand, #main_link {
    opacity: 0.8;
}
.newLookDesk {
    display: none;
}
   
.go-btn #submit1, .small-go-btn #submit1  {
    background-color: #46649b !important;
    color: #c9c9c9 !important;
    border-radius: 2px;
    height: 42px !important;
}

.small-go-btn #submit1 {
    top: -4px;
    position: relative;
}
    
.small-go-btn {
    height: 33px !important;
}
    
#submit1:hover {
    color: #eee !important;
}

.go-btn, .go-btn:hover {
    background-color: transparent;
    border: none !important;
	border-radius: 0px;
}

.addsettings.startmail_link {
	color: #4d71b2 !important;
}

#addb, .icon_home_prop {
    border: none !important;
}
    
#search_form .first.midline_left table td.first {
    padding-right: 0px !important;
}
    
.settings_cont.inner-pages #menu-icon-id, #menu-icon-id {
    background: none !important;
    height: 6px !important;
    width: 30px !important;
    margin: 0;
    font-size: 0;
    display: block;
    padding: 0px 0px;
    position: relative;
    top: -8px;
    left: 22px;
    border-top: 18px double #46649b;
    border-bottom: 6px solid #46649b;
    opacity: 0.7 !important;
}
    
.settings_cont.inner-pages #menu-icon-id:hover, #menu-icon-id:hover {
    opacity: 0.55 !important;
}
    
#menu-icon-id {
    padding: 0px 0px 0px 0px !important;;
    opacity: 0.7 !important;
    width: 30px !important;
}
    
#home_wrap .settings_cont {
    top: 28px !important;
    right: 40px !important;
}
    
#addtobrowser:hover #add-browser, #setashome:hover #set-home {
    border-bottom: solid 1px #3C733D !important;
}

.wrapper-dropdown {
    border-color: #5778AD !important;
}
    
.wrapper-dropdown .dropdown li, .wrapper-dropdown .dropdown {
    border-color: #5778AD !important;
}
    
.wrapper-dropdown::after {
    border-top-color: #5778AD !important;
}
    
.wrapper-dropdown .dropdown .selected-option,
.select-style select {
    background-image: none !important;
    font-weight: bold;
    background-color: transparent;
}

.select-style select option {
    background: #202020 !important;
    color: #A5A5A5 !important;
}

.newLookDesk {
    background-color: #282828 !important;
    color: #4d71b2 !important;
    border-color: #4d71b2 !important;
}
}
