css:
@-moz-document url-prefix("https://trello.com/") {
body {
    background-color: #0C2A46 !important;
}
.board-drawer-content {
    background-color: transparent !important;
}
.board-drawer-content .quiet,
.board-drawer-content .quiet a,
.boards-page-board-section-header-name,
.agile_arrow_title {
    color: #EDEDED;
}
.boards-drawer {
    background-color: #0C2A46 !important;
}
.boards-drawer input {
    background-color: white;
}
.boards-drawer-header {
    background-color: #0C2A46 !important;
    color: #f3f3f3 !important;
}
}
