.icon_post_text::before,
.popover_gradient > .popover_inner a,
.help-slidedown::before,
.search_form_field::before,
.ui_search.active .search_query,
.ui_search.selected .search_query,
.search_popover .search_results_section .search_subheading,
.ui_notes .date_header, .ui_notes .date_header.date_activity,
.ui_search.active .search_form_field::before,
.ui_search.selected .search_form_field::before,
.ask-form .ask-question,
.messaging-inbox .inbox-compose .inbox-compose-input .input,
.messaging-inbox .inbox-recipients h2,
.popover--activity-popover .pop-menu p.title,
.ask-form .ask-anonymously-label,
