.ui_search .search_query,
.editor-placeholder,
.post-activity-reply .editor-slot,
.title-field .editor-slot,
textarea,
#user_to_check_for,
#tumblelog_ask_page_title,
label.current_selection,
.link-editor .editor.editor-plaintext.has-text,
.quote-field .editor.editor-plaintext.has-text,
.editor.editor-richtext,
#dashboard_group_members input.text_field,
.chat-field .editor.editor-plaintext,
