}
.post-content-text a,
.post .post_body a,
.reblog-content a
{
 text-decoration: underline;
 text-decoration-color: #fff;
}

/* --- Bullshit removal --- */

.peepr-body .indash_header_wrapper .navigation
{
 width: 583px;
}
.indash_header_wrapper .header_image .edit_button
{
 top: 80px;
}

