.messaging-conversation--open .conversation-preview,
.blog-activity .blog-activity-more,
.blog-activity .blog-activity-content,
.popover--account-popover .popover .popover_header,
.popover--account-popover .popover .popover_menu_item_blog:hover:not(:first-child),
.ui_notes .activity-notification + .activity-notification,
.post-activity-popover,
.is-followed.with-commentary .post-activity-note-content,
.is-followed.without-commentary.post-activity-note,
