.dropzone-tooltip.pinned-target,
.yamplus-unit-container,
.stretchy_kids,
.remnant-unit-container,
.remnant_ad,
li.recommended-unit-container,
.video-ad-container,
.sponsored_post,
.takeover-container .sponsored-day-media-section,
.video-ad,
.standalone-ad-container,
