}
.sidebar_nav,
.pinned_sidebar_footer
{
 box-sizing: border-box;
 width: 250px;
 padding-right: 50px;
}
.controls_section_item.flat.no_push.well
{
 position: absolute;
 bottom: 0;
}
/* Dashboard: Notifications */
.tab-notice--outlined
{
 min-width: 15px;
 height: 15px;
}
.tab-notice--outlined,
.tab_notice_value
{
 line-height: 15px;
}
/* Dashboard: Video on Left */
.dockable_video_embed.docked.post.is_video
{
 left: -145px !important;
}
.tumblelog_icon.tumblelog_icon--is_primary .tooltip_wrapper
{
 left: -30px !important;
}

/* Responsify */
.posts li,
.posts li > div,
.posts li > div img
{
 max-width: 100% !important;
 height: auto;
}

/* Links to Bottom Right */
.pinned_sidebar_footer
{
 margin-left: 0;
 left: auto !important;
 right: 15px;
 width: auto;
 background: #151926;
 z-index: 2;
}
.is_dashboard .pinned_sidebar_footer
{
 right: 0;
}
.pinned_sidebar_footer ul
{
 margin-right: 0;
}

