css:
@-moz-document url-prefix("http://rus-linux.net") {

  #left_col {
    display: none;
  }

  p {
    margin: 0 0 11px;
    font-size: 14px;
    line-height: 150%;
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
  }

  pre {
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    -ms-border-radius: 3px;
    -o-border-radius: 3px;
    border-radius: 3px;
    display: block;
    padding: 10px 15px 13px 15px;
    margin-bottom: 1em;
    background-color: #333;
    border: solid 0;
    color: #F3F3F3;
    font-family: Courier, monospace !important;
    line-height: 18px;
    overflow: auto;
  }

  tt {
      -webkit-border-radius: 3px;
      -moz-border-radius: 3px;
      -ms-border-radius: 3px;
      padding: 1px;
      border-radius: 3px;
      margin-bottom: 1em;
      background-color: #fff;
      border: solid 1px #efeee6;
      color: #f14e32;
      font-size: 14px;
      font-family: Courier, monospace !important;
      line-height: 18px;
      overflow: auto;
  }

  .ine {
    border: none;
  }

}
