@-moz-document domain("realitygaming.fr") {

#header {
    background: #872b84;
    background-image: url("https://realitygaming.fr/styles/realitygaming/images/header/header.png");
    -webkit-box-shadow: 0px 5px 5px rgba(0,0,0,0.3) inset;
    -moz-box-shadow: 0px 5px 5px rgba(0,0,0,0.3) inset;
    -khtml-box-shadow: 0px 5px 5px rgba(0,0,0,0.3) inset;
    box-shadow: 0px 5px 5px rgba(0,0,0,0.3) inset;
}
#content {
    border-top: 4px solid #872b84!important;
    -webkit-box-shadow: 0px -1px 0px rgba(0,0,0,0.2);
    -moz-box-shadow: 0px -1px 0px rgba(0,0,0,0.2);
    -khtml-box-shadow: 0px -1px 0px rgba(0,0,0,0.2);
    box-shadow: 0px -1px 0px rgba(0,0,0,0.2);
}
.sidebar .section {
    border: 1px solid rgb(106,155,179);
    border: 1px solid rgb(211,141,0);
    border: 1px solid #872b84;
    _border: 1px solid rgb(211,141,0);
}
.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
    background: rgb(249,166,0) url('https://realitygaming.fr/styles/realitygaming/images/main/pattern3-ts.png') repeat-x bottom;
    background: #872b84 url('https://realitygaming.fr/styles/realitygaming/images/main/pattern3-ts.png') repeat-x bottom;
}
.nodeList .categoryStrip {
    -webkit-border-radius: 1px!important;
    -moz-border-radius: 1px!important;
    -khtml-border-radius: 1px!important;
    border-radius: 1px!important;
    border-bottom: 3px solid #872b84!important;
    -webkit-box-shadow: 1px 2px 5px rgba(0,0,0,.2);
    -moz-box-shadow: 1px 2px 5px rgba(0,0,0,.2);
    -khtml-box-shadow: 1px 2px 5px rgba(0,0,0,.2);
    box-shadow: 1px 2px 5px rgba(0,0,0,.2);
}

.nodeList .categoryStrip {
    font-weight: bold;
    font-size: 12px;
    font-family: Arial,sans-serif;
    color: #fff;
    background: rgb(47,120,160) url('https://realitygaming.fr/styles/realitygaming/images/main/pattern3-ts.png') repeat-x bottom;
    background: rgba(47,120,160,1) url('https://realitygaming.fr/styles/realitygaming/images/main/pattern3-ts.png') repeat-x bottom;
    padding: 5px 10px;
    margin: 3px auto 0;
    -webkit-border-top-left-radius: 2px;
    -moz-border-radius-topleft: 2px;
    -khtml-border-top-left-radius: 2px;
    border-top-left-radius: 2px;
    -webkit-border-top-right-radius: 2px;
    -moz-border-radius-topright: 2px;
    -khtml-border-top-right-radius: 2px;
    border-top-right-radius: 2px;
    -webkit-border-bottom-right-radius: 0px;
    -moz-border-radius-bottomright: 0px;
    -khtml-border-bottom-right-radius: 0px;
    border-bottom-right-radius: 0px;
    -webkit-border-bottom-left-radius: 0px;
    -moz-border-radius-bottomleft: 0px;
    -khtml-border-bottom-left-radius: 0px;
    border-bottom-left-radius: 0px;
    text-shadow: 1px 1px rgba(0,0,0,0.2);
    background: rgb(47,120,160) url('https://realitygaming.fr/styles/realitygaming/images/main/pattern4-ts.png') repeat-x bottom;
    background: #872b84 url('https://realitygaming.fr/styles/realitygaming/images/main/pattern4-ts.png') repeat-x bottom;
    padding: 5px 10px;
    margin: 0;
    border: 1px solid rgb(86,130,152);
    border: 1px solid rgb(249, 166, 0);
    border: 1px solid #872b84;
    border-bottom-width: 0px;
    border-bottom-style: none;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    -khtml-border-radius: 3px;
    border-radius: 3px;
    min-height: 6px;
    -webkit-box-shadow: inset 1px 2px 5px rgba(0,0,0,0.1),1px 1px 2px rgba(0,0,0,0.1);
    -moz-box-shadow: inset 1px 2px 5px rgba(0,0,0,0.1), 1px 1px 2px rgba(0,0,0,0.1);
    -khtml-box-shadow: inset 1px 2px 5px rgba(0,0,0,0.1), 1px 1px 2px rgba(0,0,0,0.1);
    box-shadow: inset 1px 2px 5px rgba(0,0,0,0.1), 1px 1px 2px rgba(0,0,0,0.1);
}
.menuVisiteur {
    -webkit-box-shadow: 0px 2px 5px #9E6900!important;
