css:
@-moz-document url-prefix("http://www-01.ibm.com/support/knowledgecenter"), url-prefix("https://www-01.ibm.com/support/knowledgecenter"), url-prefix("http://www.ibm.com/support/knowledgecenter"), url-prefix("https://www.ibm.com/support/knowledgecenter") {

body {
        font-family:	'Proxima Nova', 'Helvetica Neue', Helvetica, Verdana, 'Microsoft YaHei' !important;
        font-size:	    12px    !important;
        line-height:	1     !important;
        word-wrap:	break-word;
        word-break:	normal; 
    }

	.js-search-result-link,.kc-search_result-title,.ibm-h4 {
		font-size:	    20px    !important;
    }

	.kc-toc-entry-wrapper {
    	padding-top: 1px;
        padding-bottom: 1px;
	}

	.kc-button-placeholder::before {
    	font-size: 20px;
    	height: 25px;
    	position: relative;
    	top: -1px;
	}

	.kc-icon::before {
    	font-size: 12px;
    	height: 20px;
    	line-height: 22px;
	}

	ul, ol {
    	/*font-size: 1rem;*/
    	line-height: 1     !important;
    	/*list-style: outside none none;*/
	}

    p, li, a, strong, dd, .ibm-type, ol > li::before, ul > li::before, .ibm-type ul > li::before, .ibm-type ol > li::before,kc-toc-item, kc-non-active-toc-highlight {
        font-family:	'Proxima Nova', 'Helvetica Neue', Helvetica, Verdana, 'Microsoft YaHei' !important;
        font-size:	    12px    !important;
        line-height:	1.2     !important;
        word-wrap:		break-word;
        word-break:		normal; 
    }

    h1, h2, h3, h4, h5, dt, .ibm-type, ol > li::before, ul > li::before {
        font-family:	'Proxima Nova', 'Helvetica Neue', Helvetica, Verdana, 'Microsoft YaHei' !important;
        line-height:	1     !important;
    }

    code, pre {
        font-family:	Inconsolata, Consolas, 'Courier New' !important;
        font-size:	    10px    !important;
        line-height:	1     !important;
        word-wrap:		break-word;
        word-break:		break-all; 
    }

    p code, p pre, li code, li pre, .option, .cmdopt, .syscmdopt {
        font-family:	Inconsolata, Consolas, 'Courier New' !important;
        font-size:	    10px    !important;
        line-height:	1     !important;
        word-wrap:		break-word;
        word-break:		break-all; 
    }

    pre.displaycode {
        font-family:	Inconsolata, Consolas, 'Courier New' !important;
        font-size:	    10px    !important;
        line-height:	1     !important;
        word-wrap:		break-word;
        word-break:		break-all; 
    }
}
