/* Replace YouTube logo on small window,
original image has white edges */

@media only screen and (max-width: 498px) and (min-width: 0px), only screen and (max-width: 704px) and (min-width: 499px) {
    .exp-responsive #yt-masthead #logo-container {
        background: no-repeat url(http://i.imgur.com/mRon4QY.png) !important;
        background-size: auto !important;
        width: 40px !important;
        height: 28px !important;
    }
    
    #yt-masthead #logo-container span.content-region {
        display: none !important;
    }                                        
}

/* Logo */
#masthead-logo img, #yt-masthead #logo-container .logo, .footer-logo img,#footer-logo .footer-logo-icon,span.fg-eh-hj.a-b-c.fg-ph-Qc-Wd {
background: url(http://i.imgur.com/cOlfpSF.png) 0 no-repeat!important;
    background-size: contain!important
}

/* Scrollbar Chrome*/
*::-webkit-scrollbar {
    width: 8px ! important;
    height: 10px ! important;
    background: #1e1e1e ! important;
    border: 1px solid #252525 ! important;
}

*::-webkit-scrollbar-button {
display: none ! important;
}

*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
border: 0 ! important;
box-shadow: none ! important;
}

*::-webkit-scrollbar-thumb {
    background: #333333 ! important;
}

*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
background: #1e1e1e ! important;
}


/*spinner replacement*/
.ISc,
.Pu5TY,
.RWdWXd,
.Tzc,
.yt-spinner-img {
    -webkit-animation: scaleout 1s infinite ease-in
}
@-webkit-keyframes scaleout {
    0% {
        -webkit-transform: scale(0)
    }
    100% {
        -webkit-transform: scale(1);
        opacity: 0
    }
}
@keyframes scaleout {
    0% {
        transform: scale(0);
        -webkit-transform: scale(0)
    }
    100% {
        transform: scale(1);
        -webkit-transform: scale(1);
        opacity: 0
    }
}
	@-webkit-keyframes scaleout {
	  0% {
	    -webkit-transform: scale(0.0)
	  }
	  100% {
	    -webkit-transform: scale(1.0);
	    opacity: 0;
	  }
	}

	@keyframes scaleout {
	  0% {
	    transform: scale(0.0);
	    -webkit-transform: scale(0.0);
	  }
	  100% {
	    transform: scale(1.0);
	    -webkit-transform: scale(1.0);
	    opacity: 0;
	  }
	}

	.yt-spinner-img,.ISc.Tzc,.Tzc,.ISc,.RWdWXd{
	  width: 20px;
	  height: 20px;
	  margin: 0 auto;
	  background-image: none!important ;
	  background-color: red !important;
	  background: red !important;
	  border-radius: 100% ;
	  -webkit-animation: scaleout 1.0s infinite ease-in;
	  animation: scaleout 1.0s infinite ease-in;
	}

