/*spinner replacement*/
.ISc,
.Pu5TY,
.RWdWXd,
.Tzc,
.yt-spinner-img {
    -webkit-animation: scaleout 1s infinite ease-in
}
@-webkit-keyframes scaleout {
    0% {
        -webkit-transform: scale(0)
    }
    100% {
        -webkit-transform: scale(1);
        opacity: 0
    }
}
@keyframes scaleout {
    0% {
        transform: scale(0);
        -webkit-transform: scale(0)
    }
    100% {
        transform: scale(1);
        -webkit-transform: scale(1);
        opacity: 0
    }
}
	@-webkit-keyframes scaleout {
	  0% {
	    -webkit-transform: scale(0.0)
	  }
	  100% {
	    -webkit-transform: scale(1.0);
	    opacity: 0;
	  }
	}

	@keyframes scaleout {
	  0% {
	    transform: scale(0.0);
	    -webkit-transform: scale(0.0);
	  }
	  100% {
	    transform: scale(1.0);
	    -webkit-transform: scale(1.0);
	    opacity: 0;
	  }
	}

	.yt-spinner-img,.ISc.Tzc,.Tzc,.ISc,.RWdWXd{
	  width: 20px;
	  height: 20px;
	  margin: 0 auto;
	  background-image: none!important ;
	  background-color: red !important;
	  background: red !important;
	  border-radius: 100% ;
	  -webkit-animation: scaleout 1.0s infinite ease-in;
	  animation: scaleout 1.0s infinite ease-in;
	}

