css:
@-moz-document regexp(".+\\bproquest\\b.+\\bsafaribooksonline\\b.+\\bcom\\b.+") {
.upper {
    background-color: rgb(15, 85, 140) !important;
}
body, .pagewrap_reader, .pagewrap_reader_fullview {
    color: rgb(15, 85, 140);
    background-color: rgb(210, 223, 232);
}
.paneL, .catalog_container p {
    font-size: initial;
}
.htmlcontent {
    max-width: 80ch !important;
    margin: auto;
}
.htmlcontent p, .htmlcontent ul {
    line-height: 2em !important;
    text-align: justify;
}
.htmlcontent p + p {
    margin-top: 1em;
    text-indent: 3em;
}
.htmlcontent dt {
    font-weight: bolder;
}
.htmlcontent p > code {
    font-weight: bolder !important;
}
.htmlcontent .epub__codeline, code, pre {
    font-family: monospace !important;
}
.navigations {
    background-color: inherit !important;
}
.navtop_left, .navbottom_left, .navtop_right, .navbottom_right {
    background-image: none;
}
}
