css:
@-moz-document url("http://play.pokemonshowdown.com/"), url-prefix("http://play.pokemonshowdown.com/"), url-prefix("http://smogtours.psim.us/") {
a.ilink {
    text-decoration: none;
    color: #ff0000;
}
a.ilink:hover {
    text-decoration: none;
    color: #e60000;
}
.backdrop:before {
    display: block;
    content: "";
    background: url(http://i.imgur.com/KP5erII.jpg?2);
    background-size: cover;
    width: 648px;
    height: 360px;
}
.roomlist a.ilink {
    font-size: 12pt;
    background: white;
    border-color: #5a5a5a;
    border-width: 3px;
}
a {
    color: #ce1010;
}
body {
    background: url(http://i.imgur.com/q66xcFC.jpg)!important;
    background-size: cover !important;
}
.highlighted {
    background: #c997c7
}
.tabbar {
    bottom: 0;
}
.userbar {
    top: 18px;
}
.userbar .username {
    text-shadow: true;
    text-shadow: 1px 1px black;
}
.mainmenufooter small:after {
    content: "E Pluribus Unum";
    font-size: 15pt;
    color: #000000;
    text-shadow: 0 1px black;
    position: absolute;
    left: 0px;
    top: -40px;
    width: 200px;
}
.mainmenu .menugroup:nth-child(1) .button,
.mainmenu .menugroup:nth-child(2) p:nth-child(1) .button,
.mainmenu .menugroup:nth-child(2) p:nth-child(2) .button,
.mainmenu .menugroup:nth-child(2) p:nth-child(3) .button,
.mainmenu .menugroup:nth-child(3) p:nth-child(1) .button,
.mainmenu .menugroup:nth-child(3) p:nth-child(2) .button,
.button.mainmenu4.onlineonly,
button.mainmenu5.onlineonly,
.rightmenu .menugroup .button {
    background: url(http://www.wesleyworks.com/Icons/Wallpaper/background-animated.gif) center left no-repeat;
    box-shadow: 0px 0px 5px #171616;
    color: white;
    border: none;
    text-shadow: 0px -1px 0px black;
}
.mainmenu .menugroup:nth-child(1) .button:hover,
.mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover,
.mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover,
.mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover,
.mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover,
.mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover,
.button.mainmenu4.onlineonly:hover,
button.mainmenu5.onlineonly:hover,
.rightmenu .menugroup .button:hover {
    border: none;
    background: url(http://www.wesleyworks.com/Icons/Wallpaper/background-animated.gif) center left no-repeat;
    color: #97b1fb;
    font-size: 22px;
}
.userbar:before {
    display: block;
    content: "";
    width: 32px;
    height: 32px;
    float: left;
    position: relative;
    top: -9px;
}
.icon-user {
    display: none;
}
.siderooms {
    background: #ffffff;
    height: 56px;
}
.maintabbarbottom {
    background: linear-gradient(to bottom, rgba(0, 0, 0, .6) 0%, rgba(0, 0, 0, 0) 100%);
    border: none;
    box-shadow: none;
    z-index: 2;
}
.header {
    height: 56px;
    background: #ffffff;
}
.header .logo {
    float: left;
    margin: 7px 0px 0 2px;
}
.tabbar .closebutton,
.tabbar a.cur+.closebutton {
    top: 19px;
}
.closebutton:hover {
    color: #ebf0fa;
}
.closebutton {
    color: #ebf0fa;
}
.maintabbar .inner {
    padding-top: 0px;
    height: 76px;
    background: #ffffff;
}
.tabbar.maintabbar {
    margin-left: 170px;
    margin-right: 0px;
    overflow: hidden;
    height: 60px;
    background-position: right;
    background-repeat: no-repeat;
}
.tabbar a.button.cur,
.tabbar a.button.cur:hover,
.tabbar a.button {
    color: #ecf0f1;
    background: #ee0909;
    box-shadow: none;
    border: none;
    cursor: default;
    top: 0px;
    border-radius: 5px;
    text-shadow: none;
    margin-left: 5px;
    margin-top: 15px;
}
.tabbar a.button:hover,
.tabbar a.button.cur {
    background: #c90d0d;
}
.tabbar a.button {
    cursor: pointer;
    text-align: center;
    white-space: nowrap;
    text-shadow: none;
    font-size: 11px;
}
.ps-popup {
    position: absolute;
    top: auto;
    right: auto;
    left: auto;
    bottom: auto;
    text-align: left;
    padding: 2px 10px;
    z-index: 20;
    border: 1px solid #181818;
    border-radius: 2px;
    box-shadow: inset 0px 0px 0px 1px rgba(0, 0, 0, .2);
    background: #e8e8e8;
}
.battle-log-add,
.chat-log-add {
    border-color: #b80a0a;
    background: #b80a0a;
    text-shadow: 1px 1px black;
}
element.style {
    color: red;
}
.message-pm {
    color: white;
}
.userlist {
    background: #c90d0d;
}
.userlist li button:hover {
    background: #f7f7fb;
}
.userlist li {
    border-bottom: 1px solid #1b1b1b;
}
.battle-log {
    background: #af1111;
    background-size: cover;
    font-weight: bold;
    color: white;
    outline: black;
    font-size: 14px;
    border-width: 4;
}
.chat-log {
    background: url(http://i.imgur.com/BWMEKEx.png);
    background-size: cover;
    font-weight: bold;
    color: Black;
    border-width: 0;
}
.username {
    text-shadow: 1px 1px black;
}
.battle-log h2 {
    position: relative;
    border: 0px solid white;
    background: #161616;
}
.battle-log h2:after {
    display: block;
    content: "";
    background: url(http://i.imgur.com/BWMEKEx.png);
    background-size: cover;
    width: 38px;
    height: 31px;
    position: absolute;
    right: 8px;
    top: -4px;
}
.battle-log .chat em {
    padding: 1px 4px 1px 3px;
    color: #eeedee;
    font-style: normal;
}
.battle-log .chat strong {
    color: #ecf0f1;
}
.challenge {
    background: White;
    border: 1px solid #2980b9;
    color: rgba(0, 0, 0, .6);
    padding: 1px 5px;
}
.pm-log em {
    color: #d4d5f9;
}
.pm-box .focused {
    background: #1b1b1b;
}
.pm-window.focused h3:hover {
    background: #3498db;
}
.pm-window h3,
.pm-window.focused h3 {
    color: #ecf0f1;
    background: #1b1b1b;
    margin: 0;
    padding: 2px 5px;
    font-size: 12pt;
    border-width: 0;
    cursor: pointer;
    border-radius: 0px;
}
.pm-log {
    background: url(http://img2.sportal.bg/uploads/news/2016_17/org/00601623.jpg);
    background-size: cover;
    opacity: 5;
    border-width: 0;
}
.pm-log-add {
    border-width: 0;
    background: #1b1b1b;
    border-radius: 0px;
}
.ladder table {
    background-color: #ecf0f1;
}
.infobox,
.ladder,
.pm-window {
    position: relative;
    border: 1px solid #181818;
    border-radius: 2px;
    box-shadow: inset 0px 0px 0px 1px rgba(0, 0, 0, .2);
    margin-top: 5px;
    color: #1b1b1b;
    background-color: #ecf0f1;
}
.utilichart {
    position: relative;
    border: 1px solid #181818;
    border-radius: 2px;
    box-shadow: inset 0px 0px 0px 1px rgba(0, 0, 0, .2);
    background: url(http://images4.fanpop.com/image/photos/16700000/red-black-wallpapers-16714783-1280-768.jpg);
    margin-top: 5px;
    min-height: 33px;
    width: 270px;
    height: 96px
}
.utilichart:after,
.infobox:after,
.ladder:after,
.pm-window:after,
.ps-popup:after,
#tooltipwrapper .tooltip:after {
    display: block;
    content: "";
    width: 2px;
    height: 3px;
    position: absolute;
    left: -48px;
    top: -8px
}
.utilichart .numcol,
.utilichart .movenamecol,
.utilichart .namecol {
    padding: 0;
    margin: 0;
    text-align: left;
    font-weight: bold;
    color: #161616;
    position: absolute;
    left: 10px;
    top: 2px;
    font-size: 8pt
}
.utilichart .pokemonnamecol {
    padding: 0;
    margin: 0;
    font-weight: bold;
    color: #161616;
    position: absolute;
    left: 40px;
    top: 2px;
    font-size: 8pt
}
.utilichart .typecol {
    width: 100px;
    height: 0;
    padding: 0;
    margin: 0;
    position: absolute;
    left: 10px;
    top: 18px
}
.utilichart .typecol img {
    opacity: 1;
    margin-right: 10px
}
.utilichart .iconcol,
.utilichart .itemiconcol {
    position: absolute;
    right: 5px;
    top: 3px;
}
.utilichart .abilitycol,
.utilichart .twoabilitycol {
    float: left;
    color: #1b1b1b;
    margin: 34px 40px 0 0px;
    width: 85px;
    font-size: 8pt
}
.utilichart .abilitycol em,
.utilichart .statcol em,
.utilichart .bstcol em,
.utilichart .twoabilitycol,
.utilichart .labelcol em,
.utilichart .widelabelcol em,
.utilichart .pplabelcol em {
    color: #DDD
}
.utilichart .statcol,
.utilichart .bstcol {
    position: relative;
    left: -5px;
    top: 3px;
    text-align: center;
    color: black;
    margin-right: 12px
}
.utilichart .itemdesccol,
.utilichart .abilitydesccol,
.utilichart .movedesccol {
    white-space: normal;
    padding-top: 7px;
    font-size: 8pt;
    width: 250px;
    height: 50px;
    overflow: visible;
    color: #1b1b1b;
    position: absolute;
    top: 28px;
    left: 10px
}
.utilichart .labelcol,
.utilichart .widelabelcol,
.utilichart .pplabelcol {
    padding-top: 0;
    height: 28px;
    width: 24px;
    text-align: center;
    font-size: 8pt;
    margin: 65px 45px 0 0
}
.ps-room.ps-room-opaque {
    background: #333;
    color: black;
}
.ps-room {
    position: absolute;
    display: block;
    top: 56px;
    left: 0;
    right: 0;
    bottom: 0;
    border: 1px solid #333;
    border-top: none;
}
.ps-room .battle {
    top: 60px;
    border-width: 0px;
}
.foehint {
    position: relative;
    top: 45px;
}
.battle {
    border: 0px solid #AAAAAA;
    width: 640px;
    height: 360px;
    overflow: visible;
    font-family: Verdana, sans-serif;
    font-size: 10pt;
}
.backdrop {
    width: 640px;
    height: 360px;
    position: absolute;
    top: 0px;
    left: 0px;
    overflow: hidden;
    background: -50px -90px;
    opacity: 1 !important;
}
.backdrop:after {
    display: block;
    content: "";
    background: url(http://i.imgur.com/ybilivg.png);
    position: absolute;
    top: 0;
    left: 0;
    width: 640px;
    height: 360px;
}
.ps-room .battle-controls {
    font-size: 16pt;
    color: black;
    background: transparent !important;
    position: inherit;
    text-align: right;
    top: 200px;
}
.sidecondition-reflect {
    background: url(http://i.imgur.com/X1XDxVB.png);
    opacity: 0.7!important;
    height: 90px!important;
    width: 130px!important;
    border-radius: 0;
    border-width: 0;
    box-shadow: none;
}
.sidecondition-safeguard {
    background: url(http://i.imgur.com/FrCoWCW.png);
    opacity: 0.6!important;
    height: 90px!important;
    width: 130px!important;
    border-radius: 0;
    border-width: 0;
    box-shadow: none;
}
.sidecondition-lightscreen {
    background: url(http://i.imgur.com/8eLftNA.png);
    opacity: 0.6!important;
    height: 90px!important;
    width: 130px!important;
    border-radius: 0;
    border-width: 0;
    box-shadow: none;
}
.turn {
    position: absolute;
    top: -42px;
    left: 270px !important;
    width: 101px;
    height: 17px;
    font-size: 8pt;
    font-weight: bold;
    margin: 0px;
    padding: 0px;
    padding-top: 35px;
    border: 0;
    border-radius: 0;
    opacity: 1 !important;
    background: url(http://i.imgur.com/Ng0wOlV.png);
    text-align: center;
    color: #FFF;
}
.rightbar {
    position: absolute;
    top: -60px;
    right: 15px;
    /* height:100px; */
    
    height: 100px;
    /*width:320px; */
    
    width: 320px;
    background-color: rgba(0, 0, 0, 0);
    border: 0px solid rgba(0, 0, 0, 0.5);
    color: #777777;
    background-image: url(http://i.imgur.com/WEdUzyR.png);
    background-position: 0px -21px;
}
.rightbar .trainer {
    margin-top: 0px;
    left: 80px;
    bottom: 20px;
}
.rightbar .trainersprite {
    position: absolute;
    top: -18px;
    left: 180px;
    opacity: 1 !important;
}
.trainer strong {
    display: block;
    font-size: 12pt;
    overflow: hidden;
    padding-top: 0px;
    padding-left: 110px;
    color: #fafafa;
    top: 50px;
}
.rightbar .trainer strong {
    display: block;
    font-size: 8pt;
    text-align: left;
    overflow: hidden;
    position: relative;
    right: 60px;
    top: 0px;
}
.rightbar .trainer .teamicons {
    float: left;
    position: relative;
    top: 0px;
    left: -8px;
}
.leftbar {
    position: absolute;
    top: -60px;
    left: 15px;
    height: 80px;
    width: 320px;
    background-color: rgba(255, 255, 255, 0);
    border: 0px solid rgba(215, 215, 215, 0.5);
    color: #777777;
    background-image: url(http://i.imgur.com/Y68Rmc5.png);
    background-position: 0px -21px;
}
.leftbar .trainer {
    margin-top: 0px;
    text-align: left;
}
.leftbar .trainersprite {
    position: absolute;
    top: 60px;
    right: 250px;
    opacity: 1 !important;
}
.leftbar .trainer strong {
    display: block;
    font-size: 8pt;
    overflow: hidden;
    text-align: right;
    position: relative;
    left: -130px;
    top: 78px;
}
.leftbar .trainer .teamicons {
    float: right;
    position: relative;
    top: 79px;
    right: 68px;
}
.healthy {
    float: right;
    color: #b4b4b4;
    position: relative;
    top: 1px;
    opacity: 0;
    right: -350px
}
.weak {
    float: right;
    position: relative;
    top: 1px;
    opacity: 0;
    right: -350px
}
.critical {
    float: right;
    position: relative;
    top: 1px;
    opacity: 0;
    right: -350px
}
.message-error {
    color: #11D3DE;
}
.weather {
    text-shadow: none;
    color: rgba(90, 0, 0, 0);
}
.weather em small {
    font-size: 8pt !important;
    color: white !important;
    text-shadow: 1px 1px black;
    text-wrap: none !important;
    position: static;
    left: 50px;
    top: 25px;
    height: 600px;
    width: 1000px;
    text-align: right;
}
.weather em {
    font-size: 12pt !important;
    color: white !important;
    text-shadow: 2px 2px black;
    text-wrap: none !important;
    opacity: 25 !important;
    font-weight: bold;
    position: relative;
    left: -100px;
    top: 5px;
    width: 95px;
    text-align: center;
}
.sunweather,
.sunnydayweather,
.desolatelandweather {
    background: url(http://media.giphy.com/media/9ImjGyntsHI3e/giphy.gif) no-repeat scroll left top;
    background-size: cover;
    width: 645px;
    height: 360px;
}
.rainweather,
.raindanceweather,
.primordialseaweather {
    background: url(http://i995.photobucket.com/albums/af71/crystalized_heart819/Dreamweaver%20Backgrounds/kiss-rain-falling-animated.gif) no-repeat scroll left top;
    background-size: cover;
    width: 648px;
    height: 360px;
}
.hailweather {
    background: url(http://www.markdubois.info/weblog/wp-content/uploads/2014/02/Snowstorm.gif) no-repeat scroll left top;
    background-size: cover;
    width: 648px;
    height: 360px;
}
.sandstormweather {
    background: url(https://33.media.tumblr.com/92020bbb9e20a7016f79ac4e7a36e5c0/tumblr_myzejpKFDa1shsw6uo1_500.gif) no-repeat scroll left top;
    background-size: cover;
    width: 648px;
    height: 360px;
}
.pseudoweather {
    background: url(http://i.imgur.com/diinudq.png) no-repeat scroll left top;
}
.whatdo {
    display: none;
}
.switchmenu button,
.switchmenu button:disabled {
    background: url(http://i.imgur.com/qYAMtSx.png) !important;
    border: none;
    box-shadow: none;
    border-radius: 0;
    font-size: 8pt;
}
.switchmenu button {
    color: white;
    text-shadow: 1px 1px black;
}
.switchmenu button:disabled {
    opacity: .5;
}
.movemenu button {
    width: 150px;
    height: 50px;
    border-radius: 0;
    border-width: 0;
    box-shadow: none;
    color: black;
    text-shadow: 1px 1px black;
    font-size: 10pt;
    padding-top: 10px;
}
.movemenu button small {
    text-align: center;
    width: 62px;
    margin-top: 2px;
}
.movemenu button.type-Normal {
    background: url(http://i.imgur.com/1ctemFU.png)
}
.movemenu button.type-Normal:hover {
    transform: rotate(0deg) scale(1.10) skew(0deg) translate(0px);
    background: url(http://i.imgur.com/vTfBrkj.png)
